mapper.select:
/* accountPortal.selectTaxInvioceList 포탈 세금계산서 목록 */ SELECT TI.TaxInvoiceID , TI.CompanyCode , TI.WriteDate , TI.TaxTotal , TI.SupplyCostTotal , TI.TotalAmount , TI.Remark1 , TI.InvoicerCorpName , DATE_FORMAT( TI.WriteDate, '%Y.%m.%d') WriteDateStr FROM covi_account4j_si.act_taxinvoice TI WHERE 1=1 AND TI.DataIndex = 'BUY'
AND ( TI.InvoiceeEmail1 IN ( SELECT AM.TaxMailAddress FROM covi_account4j_si.act_manager AM WHERE AM.ManagerUserCode = #{UR_Code} UNION SELECT SOU.MailAddress FROM covi_smart4j.sys_object_user SOU WHERE SOU.UserCode = #{UR_Code} ) OR TI.TossUserCode = #{UR_Code} )
ORDER BY TI.WriteDate DESC LIMIT 5

/* accountPortal.selectTaxInvioceListCnt 포탈 세금계산서 목록 */ SELECT Count(*) FROM covi_account4j_si.act_taxinvoice TI WHERE 1=1
AND ( TI.InvoiceeEmail1 IN ( SELECT AM.TaxMailAddress FROM covi_account4j_si.act_manager AM WHERE AM.ManagerUserCode = #{UR_Code} UNION SELECT SOU.MailAddress FROM covi_smart4j.sys_object_user SOU WHERE SOU.UserCode = #{UR_Code} ) OR TI.TossUserCode = #{UR_Code} )
AND TI.DataIndex = 'BUY'
