SELECT IFNULL(SUM(Amount),0) amount,COUNT(*) cnt FROM covi_account4j_si.act_expence_application AEA INNER JOIN covi_account4j_si.act_expence_application_list AEAL ON AEA.ExpenceApplicationID = AEAL.ExpenceApplicationID INNER JOIN covi_account4j_si.act_expence_application_div AEAD ON AEAL.ExpenceApplicationListID = AEAD.ExpenceApplicationListID LEFT OUTER JOIN covi_smart4j.sys_object_user_basegroup USR_CUD ON AEA.RegisterID = USR_CUD.UserCode AND USR_CUD.JobType = 'Origin' WHERE AEA.CompanyCode = #{companyCode} AND ProofDate LIKE CONCAT(#{payDate},'%') AND AEA.ApplicationStatus = 'E'
AND AEAD.CostCenterCode = #{deptCode}
AND AEA.RegisterID = #{UR_Code}
AND USR_CUD.DeptCode IN ( SELECT GroupCode FROM covi_smart4j.sys_object_group WHERE GroupType = 'Dept' AND GroupPath LIKE CONCAT('%;',#{stdCode},';%') )

SELECT Code, (SELECT CostCenterName FROM covi_account4j_si.act_cost_center acc WHERE acc.CostCenterCode = Code) Name, Cnt, Amount FROM (SELECT IFNULL(Code,'Total') Code, COUNT(*) Cnt, SUM(Amount) Amount FROM ( SELECT AEAD.CostCenterCode Code , 1 , AEAD.Amount Amount FROM covi_account4j_si.act_expence_application AEA INNER JOIN covi_account4j_si.act_expence_application_list AEAL ON AEA.ExpenceApplicationID = AEAL.ExpenceApplicationID INNER JOIN covi_account4j_si.act_expence_application_div AEAD ON AEAL.ExpenceApplicationListID = AEAD.ExpenceApplicationListID INNER JOIN covi_account4j_si.act_account C ON AEAD.AccountCode = c.AccountCode LEFT OUTER JOIN covi_smart4j.sys_object_user_basegroup USR_CUD ON AEA.RegisterID = USR_CUD.UserCode AND USR_CUD.JobType = 'Origin' WHERE AEA.CompanyCode = #{companyCode} AND ProofDate LIKE CONCAT(#{payDate},'%')
AND AEAD.StandardBriefID = #{accountCode}
AND AEAD.AccountCode = #{accountCode}
AND AEA.RegisterID = #{UR_Code}
AND USR_CUD.DeptCode IN ( SELECT GroupCode FROM covi_smart4j.sys_object_group WHERE GroupType = 'Dept' AND GroupPath LIKE CONCAT('%;',#{stdCode},';%') )
AND AEA.ApplicationStatus = 'E' ) A GROUP BY A.Code with ROLLUP) aa ORDER BY Amount DESC

SELECT A.payDate,B.* FROM ( SELECT CONCAT(#{payYear},payDate) AS payDate FROM ( SELECT '01' AS payDate UNION ALL SELECT '02' UNION ALL SELECT '03' UNION ALL SELECT '04' UNION ALL SELECT '05' UNION ALL SELECT '06' UNION ALL SELECT '07' UNION ALL SELECT '08' UNION ALL SELECT '09' UNION ALL SELECT '10' UNION ALL SELECT '11' UNION ALL SELECT '12' ) DT ) A LEFT OUTER JOIN ( SELECT SUBSTRING(ProofDate ,1,6) paydt, SUM(Amount) AS AmountSum
SUM(case when StandardBriefID = #{item} then Amount else 0 end) AS SUM_${item}
SUM(case when AccountCode = #{item} then Amount else 0 end) AS SUM_${item}
FROM covi_account4j_si.act_expence_application AEA INNER JOIN covi_account4j_si.act_expence_application_list AEAL ON AEA.ExpenceApplicationID = AEAL.ExpenceApplicationID INNER JOIN covi_account4j_si.act_expence_application_div AS AEAD ON AEAL.ExpenceApplicationListID = AEAD.ExpenceApplicationListID LEFT OUTER JOIN covi_smart4j.sys_object_user_basegroup USR_CUD ON AEA.RegisterID = USR_CUD.UserCode AND USR_CUD.JobType = 'Origin' WHERE AEA.CompanyCode = #{companyCode} AND ProofDate LIKE CONCAT(#{payYear},'%')
AND AEA.RegisterID = #{UR_Code}
AND USR_CUD.DeptCode IN ( SELECT GroupCode FROM covi_smart4j.sys_object_group WHERE GroupType = 'Dept' AND GroupPath LIKE CONCAT('%;',#{stdCode},';%') )
AND AEA.ApplicationStatus = 'E' GROUP BY SUBSTRING(ProofDate ,1,6) ) B ON A.payDate = B.paydt

SELECT '' AS DeptCode,'전체' AS DeptName UNION ALL SELECT acc.CostCenterCode,CostCenterName FROM covi_account4j_si.act_cost_center acc JOIN ( SELECT DISTINCT AEAD.CostCenterCode FROM covi_account4j_si.act_expence_application AEA INNER JOIN covi_account4j_si.act_expence_application_list AEAL ON AEA.ExpenceApplicationID = AEAL.ExpenceApplicationID INNER JOIN covi_account4j_si.act_expence_application_div AS AEAD ON AEAL.ExpenceApplicationListID = AEAD.ExpenceApplicationListID LEFT OUTER JOIN covi_smart4j.sys_object_user_basegroup USR_CUD ON AEA.RegisterID = USR_CUD.UserCode AND USR_CUD.JobType = 'Origin' WHERE AEA.CompanyCode = #{companyCode} AND ProofDate LIKE CONCAT(#{payDate},'%')
AND USR_CUD.DeptCode IN ( SELECT GroupCode FROM covi_smart4j.sys_object_group WHERE GroupType = 'Dept' AND GroupPath LIKE CONCAT('%;',#{stdCode},';%') )
) A ON acc.CostCenterCode = a.CostCenterCode

SELECT IFNULL(ExecuteDate,'TOTAL') ExecuteDate ,SUM( UsedAmount) UsedAmount ,SUM( CASE WHEN Status = 'C' THEN UsedAmount ELSE 0 END ) complete ,SUM( CASE WHEN Status = 'P' THEN UsedAmount ELSE 0 END ) pending FROM ( SELECT ExecuteDate ,IFNULL(UsedAmount,0) UsedAmount ,Status FROM ( SELECT CONCAT(#{payYear},mon) AS ExecuteDate FROM ( SELECT '01' AS mon UNION ALL SELECT '02' UNION ALL SELECT '03' UNION ALL SELECT '04' UNION ALL SELECT '05' UNION ALL SELECT '06' UNION ALL SELECT '07' UNION ALL SELECT '08' UNION ALL SELECT '09' UNION ALL SELECT '10' UNION ALL SELECT '11' UNION ALL SELECT '12' ) DT )DT LEFT OUTER JOIN ( SELECT DATE_FORMAT( EXR.ExecuteDate, '%Y%m') AS ExecuteYM ,EXR.UsedAmount ,EXR.Status FROM covi_budget4j.bem_execute_regist EXR WHERE exr.CompanyCode = #{companyCode} AND AccountCode = #{accountCode}
#{list}
AND Status != 'R' AND InitiatorID = #{UR_Code} AND InitiatorDeptCode = #{stdCode} AND DATE_FORMAT( EXR.ExecuteDate, '%Y') = #{payYear} ) A ON DT.ExecuteDate = A.ExecuteYM ) A GROUP BY ExecuteDate with ROLLUP

		SELECT 		IFNULL( SUM(BudgetAmount),0 ) BudgetAmount, BaseTerm, covi_account4j_si.Fn_GetBaseCodeName('BaseTerm', BaseTerm,'ALL') BaseTermName
		FROM 		covi_budget4j.bem_regist REG
		INNER JOIN 	covi_smart4j.sys_object_user_basegroup USR_CUD ON REG.RegisterID = USR_CUD.UserCode AND USR_CUD.JobType = 'Origin'
		WHERE 		reg.CompanyCode = #{companyCode}
		AND         REG.FiscalYear = #{payYear}
		AND			REG.BaseTerm = 'Year'
		AND			REG.IsUse	=	'Y'
		AND			REG.AccountCode = #{accountCode}

			 #{list}


		AND	USR_CUD.UserCode = #{UR_Code} 


   		AND			USR_CUD.DeptCode IN (			
					SELECT  GroupCode
					FROM 	covi_smart4j.sys_object_group
					WHERE 	GroupType = 'Dept'
					AND		GroupPath LIKE CONCAT('%;',#{stdCode},';%')
		)

SELECT AEAD.CostCenterCode , (SELECT CostCenterName FROM covi_account4j_si.act_cost_center ACC WHERE ACC.CostCenterCode = AEAD.CostCenterCode) AS CostCenterName , AEA.RegisterID , SOU.DisplayName AS RegisterName , DATE_FORMAT( AEAL.ProofDate, '%Y.%m.%d') AS ProofDate , AEAD.AccountCode , AEAD.AccountName , AEAD.StandardBriefID , AEAD.StandardBriefName , AEAD.UsageComment , AEAD.Amount FROM covi_account4j_si.act_expence_application AEA INNER JOIN covi_account4j_si.act_expence_application_list AEAL ON AEA.ExpenceApplicationID = AEAL.ExpenceApplicationID INNER JOIN covi_account4j_si.act_expence_application_div AEAD ON AEAL.ExpenceApplicationListID = AEAD.ExpenceApplicationListID LEFT OUTER JOIN covi_smart4j.sys_object_user SOU ON AEA.RegisterID = SOU.UserCode LEFT OUTER JOIN covi_smart4j.sys_object_user_basegroup USR_CUD ON AEA.RegisterID = USR_CUD.UserCode AND USR_CUD.JobType = 'Origin' WHERE AEA.CompanyCode = #{CompanyCode} AND AEA.ApplicationStatus = 'E' AND AEAL.ProofDate LIKE CONCAT(#{ProofMonth},'%')
AND AEAD.CostCenterCode = #{CostCenterCode}
AND USR_CUD.DeptCode IN ( SELECT GroupCode FROM covi_smart4j.sys_object_group WHERE GroupType = 'Dept' AND GroupPath LIKE CONCAT('%;',#{DeptCode},';%') )
AND AEAD.AccountName = #{AccountName}
AND SOU.DisplayName LIKE CONCAT('%', #{RegisterName}, '%')
AND AEAD.AccountCode = #{AccountCode}
AND AEAD.StandardBriefName = #{StandardBriefName}
AND AEAD.StandardBriefID = #{StandardBriefID}
AND AEAD.UsageComment LIKE CONCAT('%', #{UsageComment}, '%')
AND AEAD.Amount >= #{SearchAmtSt}
AND AEAD.Amount <= #{SearchAmtEd}
,AEAD.Amount DESC
,
CostCenterName RegisterName ProofDate AccountName StandardBriefName UsageComment Amount
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}


		SELECT COUNT(*)
		FROM        covi_account4j_si.act_expence_application AEA
		INNER JOIN  covi_account4j_si.act_expence_application_list AEAL			ON AEA.ExpenceApplicationID = AEAL.ExpenceApplicationID
		INNER JOIN  covi_account4j_si.act_expence_application_div AEAD			ON AEAL.ExpenceApplicationListID = AEAD.ExpenceApplicationListID
		LEFT OUTER JOIN covi_smart4j.sys_object_user SOU 						ON AEA.RegisterID = SOU.UserCode
		LEFT OUTER JOIN covi_smart4j.sys_object_user_basegroup USR_CUD 			ON AEA.RegisterID = USR_CUD.UserCode AND USR_CUD.JobType = 'Origin'	
		WHERE   AEA.CompanyCode = #{CompanyCode}
		AND     AEA.ApplicationStatus = 'E'
		AND     AEAL.ProofDate	LIKE CONCAT(#{ProofMonth},'%')		


    	AND		AEAD.CostCenterCode = #{CostCenterCode}



   		AND			USR_CUD.DeptCode IN (			
					SELECT  GroupCode
					FROM 	covi_smart4j.sys_object_group
					WHERE 	GroupType = 'Dept'
					AND		GroupPath LIKE CONCAT('%;',#{DeptCode},';%')
		)




		AND		AEAD.AccountName = #{AccountName}


		AND		SOU.DisplayName LIKE CONCAT('%', #{RegisterName}, '%')


		AND		AEAD.AccountCode = #{AccountCode}


		AND		AEAD.StandardBriefName = #{StandardBriefName}


		AND		AEAD.StandardBriefID = #{StandardBriefID}


		AND		AEAD.UsageComment LIKE CONCAT('%', #{UsageComment}, '%')


		AND		AEAD.Amount  >=  #{SearchAmtSt} 


		AND		AEAD.Amount  <=  #{SearchAmtEd}

SELECT VB.BankName , VB.BankAccountNo , TRUNCATE(EAL.RealPayAmount,0) AS TotalAmount , ( CASE WHEN VB.BankAccountName IS NULL OR VB.BankAccountName = '' THEN (CASE WHEN EAL.VendorName IS NULL OR EAL.VendorName = '' THEN ATI.InvoicerCorpName ELSE EAL.VendorName END) ELSE VB.BankAccountName END ) AS BankAccountName FROM covi_account4j_si.act_expence_application_list EAL LEFT OUTER JOIN covi_account4j_si.act_vendor V ON V.VendorNo = EAL.VendorNo LEFT OUTER JOIN ( SELECT VENDORID, MAX(BANKID) AS BankID FROM COVI_ACCOUNT4J_SI.act_vendor_bank SUB GROUP BY SUB.VENDORID ) VB2 ON VB2.VendorID = V.VendorID LEFT OUTER JOIN COVI_ACCOUNT4J_SI.act_vendor_bank VB ON VB.VendorID = V.VendorID AND VB.BankID = VB2.BankID LEFT OUTER JOIN covi_account4j_si.act_taxinvoice ATI ON EAL.TaxUID = ATI.TaxInvoiceID WHERE EAL.ExpenceApplicationListID IN
#{item}
ORDER BY ExpenceApplicationListID
