mapper.select:
SELECT	count(AR.ReceiptID)
FROM	covi_account4j_si.act_receipt AR
WHERE	 AR.RegistDate between STR_TO_DATE(CONCAT(#{payDate},'01'),'%Y%m%d') AND LAST_DAY(STR_TO_DATE(CONCAT(#{payDate},'01'),'%Y%m%d')) 
AND      AR.RegisterID  = #{UR_Code}
AND AR.CompanyCode = #{CompanyCode}

/* account.portalHome.getDeadlineInfo */ SELECT DL.CompanyCode , DL.DeadlineID , DL.NoticeText , DATE_FORMAT(DL.DeadlineStartDate,'%Y.%m.%d') AS DeadlineStartDate , DATE_FORMAT(DL.DeadlineFinishDate,'%Y.%m.%d') AS DeadlineFinishDate , IFNULL(NoticeText,'') NoticeText , DL.StandardMonth , DL.IsUse AS IsUse , DL.Control AS Control FROM covi_account4j_si.act_deadline DL WHERE
DL.CompanyCode = #{companyCode}
DL.CompanyCode in ('ALL', #{companyCode}) ORDER BY IF( CompanyCode='ALL',9,0)
LIMIT 1
