mapper.select:
/* baseCode.selectBaseCode 기초코드 조회 */
SELECT CD.BaseCodeID , CD.CompanyCode , covi_account4j_si.Fn_GetBaseCodeName('CompanyCode', CD.CompanyCode, #{companyCode}) AS CompanyName , CD.CodeGroup , CASE WHEN CD.IsGroup = 'Y' THEN CD.CodeName ELSE GrpCD.CodeName END AS CodeGroupName , CD.Code , CD.SortKey , CD.IsUse , CD.CodeName , CD.Reserved1 , CD.Reserved2 , CD.Reserved3 , CD.Reserved4 , CD.ReservedInt , CD.ReservedFloat , CD.Description , CD.ModifierID , CD.ModifierID AS ModifierName , CD.IsGroup , CD.RegisterID , CD.ModifyDate AS ModifyDate , CD.RegistDate AS RegistDate FROM covi_account4j_si.act_base_code CD LEFT OUTER JOIN covi_account4j_si.act_base_code GrpCD ON GrpCd.IsGroup = 'Y' AND GrpCD.Code = Cd.CodeGroup AND GrpCD.CompanyCode = CD.CompanyCode WHERE 1=1
AND CD.CodeGroup = #{searchGrp}
AND (UPPER(CD.CodeName) LIKE UPPER(CONCAT('%', #{searchText}, '%')) OR UPPER(CD.Code) LIKE UPPER(CONCAT('%', #{searchText}, '%')))
AND CD.CompanyCode = #{companyCode}
AND (UPPER(CD.CodeGroup) LIKE UPPER(CONCAT('%', #{searchGrpText}, '%')) OR UPPER(GrpCD.CodeName) LIKE UPPER(CONCAT('%', #{searchGrpText}, '%')))
, CD.CompanyCode , CD.CodeGroup , CD.SortKey , CD.Code
,
CompanyCode IsGroup CodeGroup CodeGroupName Code CodeName IsUse ModifierName ModifyDate
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

/* baseCode.selectBaseCodeCnt 기초코드 페이징용 갯수 카운트 */
SELECT COUNT(*) CNT FROM covi_account4j_si.act_base_code CD LEFT OUTER JOIN covi_account4j_si.act_base_code GrpCD ON GrpCd.IsGroup = 'Y' AND GrpCD.Code = Cd.CodeGroup AND GrpCD.CompanyCode = CD.CompanyCode WHERE 1=1
AND CD.CodeGroup = #{searchGrp}
AND (UPPER(CD.CodeName) LIKE UPPER(CONCAT('%', #{searchText}, '%')) OR UPPER(CD.Code) LIKE UPPER(CONCAT('%', #{searchText}, '%')))
AND CD.CompanyCode = #{companyCode}
AND (UPPER(CD.CodeGroup) LIKE UPPER(CONCAT('%', #{searchGrpText}, '%')) OR UPPER(GrpCD.CodeName) LIKE UPPER(CONCAT('%', #{searchGrpText}, '%')))

/* baseCode.selectBaseCodeView 기초코드 조회 */
SELECT CD.BaseCodeID , CD.CompanyCode , covi_account4j_si.Fn_GetBaseCodeName('CompanyCode', CD.CompanyCode, #{companyCode}) AS CompanyName , CD.CodeGroup , CASE WHEN CD.IsGroup = 'Y' THEN CD.CodeName ELSE GrpCD.CodeName END AS CodeGroupName , CD.Code , CD.SortKey , CD.IsUse , CD.CodeName , CD.Reserved1 , CD.Reserved2 , CD.Reserved3 , CD.Reserved4 , CD.ReservedInt , CD.ReservedFloat , CD.Description , CD.ModifierID , CD.ModifierID AS ModifierName , CD.IsGroup , CD.RegisterID , CD.ModifyDate AS ModifyDate , CD.RegistDate AS RegistDate FROM covi_account4j_si.act_base_code CD LEFT OUTER JOIN covi_account4j_si.act_base_code GrpCD ON GrpCd.IsGroup = 'Y' AND GrpCD.Code = Cd.CodeGroup AND GrpCD.CompanyCode = CD.CompanyCode
WHERE 1=1 AND CD.CodeGroup = #{searchGrp}
AND UPPER(CD.CodeName) LIKE UPPER(CONCAT('%', #{searchText}, '%'))
AND CD.CompanyCode = #{companyCode}
AND CD.IsGroup = 'N'
, CD.CompanyCode , CD.CodeGroup , CD.SortKey , CD.Code
,
CompanyCode Code CodeName IsUse RegistDate ModifyDate
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

/* baseCode.selectBaseCodeCnt 기초코드 페이징용 갯수 카운트 */
SELECT COUNT(*) CNT FROM covi_account4j_si.act_base_code CD LEFT OUTER JOIN covi_account4j_si.act_base_code GrpCD ON GrpCd.IsGroup = 'Y' AND GrpCD.Code = Cd.CodeGroup AND GrpCD.CompanyCode = CD.CompanyCode WHERE 1=1
AND CD.CodeGroup = #{searchGrp}
AND UPPER(CD.CodeName) LIKE UPPER(CONCAT('%', #{searchText}, '%'))
AND CD.CompanyCode = #{companyCode}
AND CD.IsGroup = 'N'

/* baseCode.selectBaseGrpCode4Delete 기초코드 조회 */
SELECT CD.BaseCodeID , CD.Code FROM covi_account4j_si.act_base_code CD WHERE
#{item}
1=2

/* baseCode.selectBaseCodeExcel 기초코드 조회 */ SELECT CD.IsGroup , CD.CompanyCode , covi_account4j_si.Fn_GetBaseCodeName('CompanyCode', CD.CompanyCode, #{companyCode}) AS CompanyName , CD.CodeGroup , CASE WHEN CD.IsGroup = 'Y' THEN CD.CodeName ELSE GrpCD.CodeName END AS CodeGroupName , CD.Code , CD.CodeName , CD.IsUse , CD.ModifierID AS ModifierName , CD.ModifyDate AS ModifyDate FROM covi_account4j_si.act_base_code CD LEFT OUTER JOIN covi_account4j_si.act_base_code GrpCD ON GrpCd.IsGroup = 'Y' AND GrpCD.Code = Cd.CodeGroup AND GrpCD.CompanyCode = CD.CompanyCode WHERE 1=1
AND CD.CodeGroup = #{searchGrp}
AND UPPER(CD.CodeName) LIKE UPPER(CONCAT('%',#{searchText},'%'))
AND CD.CompanyCode = #{companyCode}
ORDER BY CD.CompanyCode , CD.CodeGroup , CD.SortKey , CD.Code

mapper.update:


 	    /*
 	    	baseCode.changeBaseCodeIsUse
 	    	기초코드 사용여부 수정
 	    */
		UPDATE covi_account4j_si.act_base_code
		SET IsUse = #{isUseValue} 
		, ModifierID = #{UserId}
		, ModifyDate = now(3)
		WHERE BaseCodeID = #{BaseCodeID}



 	    /*
 	    	baseCode.updateBaseCode
 	    	기초코드 수정
 	    */
		UPDATE covi_account4j_si.act_base_code
		SET IsUse = #{IsUse} 
			, CompanyCode = #{CompanyCode}
			, Description = #{Description} 
			, SortKey = #{SortKey} 
			, CodeName = #{CodeName}			
			, Reserved1 = #{Reserved1} 
			, Reserved2 = #{Reserved2} 
			, Reserved3 = #{Reserved3} 
			, Reserved4 = #{Reserved4} 
			, ReservedInt = #{ReservedInt} 
			, ReservedFloat = #{ReservedFloat} 
			, ModifierID = #{SessionUser}
			, ModifyDate = now(3)
		WHERE BaseCodeID = #{BaseCodeID}



 	    /*
 	    	baseCode.updateBaseGrpCode
 	    	기초코드 그룹코드 수정
 	    */
		UPDATE covi_account4j_si.act_base_code
		SET IsUse = #{IsUse} 
			, CompanyCode = #{CompanyCode}
			, CodeName = #{CodeGroupName} 
			, ModifierID = #{SessionUser}
			, ModifyDate = now(3)

			, Description = #{Description} 
			, Reserved1 = #{Reserved1} 
			, Reserved2 = #{Reserved2} 
			, Reserved3 = #{Reserved3} 
			, Reserved4 = #{Reserved4} 
			, ReservedInt = #{ReservedInt} 
			, ReservedFloat = #{ReservedFloat} 
		WHERE BaseCodeID = #{BaseCodeID}

