mapper.select:
SELECT * FROM ( SELECT ABR.BizTripRequestID, ABA.BizTripApplicationID, ABR.CompanyCode, covi_account4j_si.Fn_GetBaseCodeName('CompanyCode', ABR.CompanyCode, #{companyCode}) CompanyName, ABR.RequestTitle, ABR.ProcessID, (SELECT FormInstID FROM covi_approval4j.jwf_process P WHERE ABR.ProcessID = P.ProcessID) FormInstID, ABR.RequesterDeptName, ABR.RequesterName, ABR.BizTripType, covi_account4j_si.Fn_GetBaseCodeName('BizTripType', ABR.BizTripType, #{companyCode}) BizTripTypeName, ABR.ProjectCode, ABR.ProjectName, ABR.StartDate, ABR.EndDate, ABR.BusinessAreaType, covi_account4j_si.Fn_GetBaseCodeName('BusinessAreaType', ABR.BusinessAreaType, #{companyCode}) BusinessAreaTypeName, ABR.BusinessArea, ABR.RequestDate, ABR.RequestAmount, ABR.IsExceptApplication, ABA.ApplicationDate, ABA.Amount, ABR.RequestStatus, covi_account4j_si.Fn_GetBaseCodeName('ExpenceApplicationStatus', ABR.RequestStatus, #{companyCode}) RequestStatusName, ABA.ApplicationStatus, covi_account4j_si.Fn_GetBaseCodeName('ExpenceApplicationStatus', ABA.ApplicationStatus, #{companyCode}) ApplicationStatusName, ABA.ProcessID AS AppProcessID, (SELECT FormInstID FROM covi_approval4j.jwf_process P WHERE ABA.ProcessID = P.ProcessID) AppFormInstID, ABA.ExpenceApplicationID FROM covi_account4j_si.act_biztrip_request AS ABR LEFT OUTER JOIN covi_account4j_si.act_biztrip_application AS ABA ON ABR.BizTripRequestID = ABA.BizTripRequestID AND ABA.ApplicationStatus != 'DELETE' WHERE 1=1
AND ABR.CompanyCode = #{companyCode}
AND ABR.RequesterID = #{SessionUser}
AND ABR.RequestTitle LIKE CONCAT('%',#{RequestTitle},'%')
AND ABR.RequesterDeptName LIKE CONCAT('%',#{RequesterDeptName},'%')
AND ABR.RequesterName LIKE CONCAT('%',#{RequesterName},'%')
AND ABR.BizTripType = #{BizTripType}
AND ABR.ProjectCode = #{ProjectCode}
AND ABR.BusinessAreaType = #{BusinessAreaType}
AND ABR.StartDate >= #{StartDate}
AND ABR.EndDate <= #{EndDate}
AND ABR.RequestStatus = #{RequestStatus}
AND ABA.ApplicationStatus = #{ApplicationStatus}
) A
, A.RequestDate DESC
,
CompanyCode RequestTitle RequesterDeptName RequesterName BizTripTypeName ProjectName ApplicationStatusName StartDate EndDate BusinessAreaTypeName BusinessArea RequestDate RequestAmount ApplicationDate Amount
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

SELECT * FROM ( SELECT ABR.BizTripRequestID, ABA.BizTripApplicationID, ABR.CompanyCode, covi_account4j_si.Fn_GetBaseCodeName('CompanyCode', ABR.CompanyCode, #{companyCode}) CompanyName, ABR.RequestTitle, ABR.ProcessID, (SELECT FormInstID FROM covi_approval4j.jwf_process P WHERE ABR.ProcessID = P.ProcessID) FormInstID, ABR.RequesterDeptName, ABR.RequesterName, ABR.BizTripType, covi_account4j_si.Fn_GetBaseCodeName('BizTripType', ABR.BizTripType, #{companyCode}) BizTripTypeName, ABR.ProjectCode, ABR.ProjectName, ABR.StartDate, ABR.EndDate, ABR.BusinessAreaType, covi_account4j_si.Fn_GetBaseCodeName('BusinessAreaType', ABR.BusinessAreaType, #{companyCode}) BusinessAreaTypeName, ABR.BusinessArea, ABR.RequestDate, ABR.RequestAmount, ABR.IsExceptApplication, ABA.ApplicationDate, ABA.Amount, ABR.RequestStatus, covi_account4j_si.Fn_GetBaseCodeName('ExpenceApplicationStatus', ABR.RequestStatus, #{companyCode}) RequestStatusName, ABA.ApplicationStatus, covi_account4j_si.Fn_GetBaseCodeName('ExpenceApplicationStatus', ABA.ApplicationStatus, #{companyCode}) ApplicationStatusName, ABA.ProcessID AS AppProcessID, (SELECT FormInstID FROM covi_approval4j.jwf_process P WHERE ABA.ProcessID = P.ProcessID) AppFormInstID, ABA.ExpenceApplicationID FROM covi_account4j_si.act_biztrip_request AS ABR LEFT OUTER JOIN covi_account4j_si.act_biztrip_application AS ABA ON ABR.BizTripRequestID = ABA.BizTripRequestID AND ABA.ApplicationStatus != 'DELETE' WHERE 1=1
AND ABR.CompanyCode = #{companyCode}
AND ABR.RequesterID = #{SessionUser}
AND ABR.RequestTitle LIKE CONCAT('%',#{RequestTitle},'%')
AND ABR.RequesterDeptName LIKE CONCAT('%',#{RequesterDeptName},'%')
AND ABR.RequesterName LIKE CONCAT('%',#{RequesterName},'%')
AND ABR.BizTripType = #{BizTripType}
AND ABR.ProjectCode = #{ProjectCode}
AND ABR.BusinessAreaType = #{BusinessAreaType}
AND ABR.StartDate >= #{StartDate}
AND ABR.EndDate <= #{EndDate}
AND ABR.RequestStatus = #{RequestStatus}
AND ABA.ApplicationStatus = #{ApplicationStatus}
) A
, A.RequestDate DESC
,
CompanyCode RequestTitle RequesterDeptName RequesterName BizTripTypeName ProjectName ApplicationStatusName StartDate EndDate BusinessAreaTypeName BusinessArea RequestDate RequestAmount ApplicationDate Amount
ASC DESC

SELECT COUNT(ABR.BizTripRequestID)
FROM covi_account4j_si.act_biztrip_request AS ABR
LEFT OUTER JOIN covi_account4j_si.act_biztrip_application AS ABA
ON ABR.BizTripRequestID = ABA.BizTripRequestID AND ABA.ApplicationStatus != 'DELETE'
WHERE 1=1

AND ABR.CompanyCode = #{companyCode}


AND ABR.RequesterID = #{SessionUser}


AND ABR.RequestTitle LIKE CONCAT('%',#{RequestTitle},'%')


AND ABR.RequesterDeptName LIKE CONCAT('%',#{RequesterDeptName},'%')


AND ABR.RequesterName LIKE CONCAT('%',#{RequesterName},'%')


AND ABR.BizTripType = #{BizTripType}


AND ABR.ProjectCode = #{ProjectCode}


AND ABR.BusinessAreaType = #{BusinessAreaType}


AND ABR.StartDate >= #{StartDate}


AND ABR.EndDate <= #{EndDate}


AND ABR.RequestStatus = #{RequestStatus}


AND ABA.ApplicationStatus = #{ApplicationStatus}

mapper.update:
UPDATE covi_account4j_si.act_biztrip_application A
LEFT OUTER JOIN covi_account4j_si.act_biztrip_request B
ON A.BizTripRequestID = B.BizTripRequestID
SET 
	A.CompanyCode = #{CompanyCode}
	, A.ApplicationTitle = #{ApplicationTitle}
	, A.ApplicationStatus = #{ApplicationStatus}

	, A.ApplicationDate = NOW(3)

	, A.ProcessID = #{ProcessID}
	, A.UserID = B.RequesterID
	, A.UserName = B.RequesterName
	, A.StartDate = B.StartDate
	, A.EndDate = B.EndDate
	, A.BusinessArea = B.BusinessArea
	, A.BusinessPurpose = B.BusinessPurpose
	, A.BusinessDay = B.BusinessDay
	, A.WorkingDay = B.WorkingDay
	, A.Amount = #{ReqAmt}
	, A.BizTripNoteMap = #{BizTripNoteMap}
	, A.ModifierID = #{SessionUser}
	, A.ModifyDate = NOW(3)
WHERE A.ExpenceApplicationID = #{ExpenceApplicationID}


 	    UPDATE covi_account4j_si.act_biztrip_application
 	    SET ApplicationStatus = #{ApplicationStatus}

			, ProcessID = #{ProcessID}


			, ApplicationDate = NOW(3)

			, ModifierID = #{SessionUser}
			, ModifyDate =  NOW(3)
		WHERE ExpenceApplicationID = #{ExpenceApplicationID}

UPDATE covi_account4j_si.act_biztrip_request
SET IsExceptApplication = #{isExceptApplication}
