mapper.select:
SELECT a.ExecuteID , a.FiscalYear , a.CostCenterType , covi_account4j_si.Fn_GetBaseCodeName('BudgetType', a.CostCenterType, #{companyCode}) AS CostCenterTypeName , a.CostCenter , a.CostCenterName , a.AccountCode , a.AccountName , a.StandardBriefID , ifnull(d.StandardBriefName,'') StandardBriefName , DATE_FORMAT(a.ExecuteDate,'%Y.%m.%d') ExecuteDate , a.UsedAmount , a.Description , a.Status , case when STATUS = '' then '상태없음' ELSE covi_account4j_si.Fn_GetBaseCodeName('ExcuteStatus',a.Status,'ALL') end AS StatusName , a.ReservedStr1 , a.FormInstID , a.ExpenceApplicationListID , ProcessID , a.CompanyCode , CompanyCodeName FROM ( SELECT a.ExecuteID , a.FiscalYear , b.CostCenterType , a.BudgetCode AS CostCenter , b.CostCenterName , c.Accountid , a.AccountCode , c.AccountName , a.StandardBriefID , a.ExecuteDate , a.UsedAmount , a.Description , a.Status , a.ReservedStr1 , a.FormInstID , a.ExpenceApplicationListID , ProcessID , a.CompanyCode , covi_account4j_si.Fn_GetBaseCodeName('CompanyCode',a.CompanyCode, #{companyCode}) CompanyCodeName FROM covi_budget4j.bem_execute_regist a JOIN covi_budget4j.bem_regist r on a.CompanyCode = r.CompanyCode and a.FiscalYear = r.FiscalYear and a.AccountCode = r.AccountCode AND a.StandardBriefid = r.StandardBriefid AND a.ExecuteDate BETWEEN r.ValidFrom AND r.ValidTo AND a.BudgetCode = r.CostCenter JOIN covi_budget4j.vw_cost_center b ON a.CompanyCode = b.CompanyCode AND a.BudgetCode = b.CostCenterCode JOIN covi_account4j_si.act_account c ON a.CompanyCode = c.CompanyCode AND a.AccountCode = c.AccountCode LEFT JOIN covi_account4j_si.act_expence_application_list l ON a.ExpenceApplicationListID = l.ExpenceApplicationListID LEFT JOIN covi_account4j_si.act_expence_application p ON p.ExpenceApplicationID = l.ExpenceApplicationID WHERE a.companyCode = #{companyCode} AND a.FiscalYear = #{fiscalYear}
AND b.CostCenterType = #{costCenterType}
AND b.costCenterName LIKE CONCAT('%', #{costCenterName},'%')
AND ( c.AccountCode LIKE CONCAT('%',#{searchStr},'%') OR C.AccountName LIKE CONCAT('%',#{searchStr},'%') OR C.AccountShortName LIKE CONCAT('%',#{searchStr},'%') )
AND C.AccountCode LIKE CONCAT('%',#{searchStr},'%')
AND C.AccountName LIKE CONCAT('%',#{searchStr},'%')
AND C.AccountShortName LIKE CONCAT('%',#{searchStr},'%')
AND a.BudgetCode IN ( SELECT u.CostCenterCode FROM covi_account4j_si.act_user_cost_center u WHERE u.CompanyCode = a.CompanyCode AND u.CostCenterCode = a.BudgetCode AND u.usercode = #{UR_Code} UNION SELECT #{UR_Code} FROM DUAL )
) a LEFT JOIN covi_account4j_si.act_standard_brief d ON a.AccountID = d.AccountID AND a.StandardBriefID = d.StandardBriefID
,a.CostCenter , a.AccountCode, a.StandardBriefID desc
FiscalYear CostCenterTypeName CostCenterName AccountCode AccountName ExecuteDate UsedAmount Description Status CompanyCodeName
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

SELECT CostCenterType
FROM covi_budget4j.bem_regist r
JOIN covi_budget4j.vw_cost_center c ON r.CompanyCode = c.CompanyCode AND r.CostCenter = c.CostCenterCode
WHERE 	r.AccountCode = #{accountCode}
AND 	r.StandardBriefID = #{standardBriefID}
AND		r.CompanyCode = #{companyCode}


 		AND	r.FiscalYear = #{fiscalYear}




		 		AND	r.FiscalYear = (SELECT MAX(FiscalYear) FROM covi_budget4j.bem_base_term WHERE CompanyCode = #{companyCode} AND STR_TO_DATE(#{executeDate},'%Y%m%d') BETWEEN ValidFrom AND ValidTo)


		 		AND	r.FiscalYear = (SELECT MAX(FiscalYear) FROM covi_budget4j.bem_base_term WHERE CompanyCode = #{companyCode} AND NOW(3) BETWEEN ValidFrom AND ValidTo)




GROUP BY CostCenterType

SELECT CostCenterType
FROM covi_budget4j.bem_regist r
JOIN covi_budget4j.vw_cost_center c ON r.CompanyCode = c.CompanyCode AND r.CostCenter = c.CostCenterCode
WHERE 	r.AccountCode = #{accountCode}
AND 	r.StandardBriefID = #{standardBriefID}
AND		r.CompanyCode = #{companyCode}


 		AND	r.FiscalYear = #{fiscalYear}




		 		AND	r.FiscalYear = (SELECT MAX(FiscalYear) FROM covi_budget4j.bem_base_term WHERE CompanyCode = #{companyCode} AND STR_TO_DATE(#{executeDate},'%Y%m%d') BETWEEN ValidFrom AND ValidTo)


		 		AND	r.FiscalYear = (SELECT MAX(FiscalYear) FROM covi_budget4j.bem_base_term WHERE CompanyCode = #{companyCode} AND NOW(3) BETWEEN ValidFrom AND ValidTo)




GROUP BY CostCenterType

	SELECT max(budgetAmount) BudgetAmount, IsControl, IsUse
			, sum(case when STATUS='P' then usedAmount ELSE 0 end)  PendingAmount
			, sum(case when STATUS='C' then usedAmount ELSE 0 end)  CompletAmount
			, max(budgetAmount) - ifnull( sum(case when STATUS in ('P','C') then usedAmount else 0 END),0)  RemainAmount
	FROM  covi_budget4j.bem_regist r
	LEFT JOIN covi_budget4j.bem_execute_regist e ON  r.CompanyCode = e.CompanyCode AND  r.FiscalYear = e.FiscalYear AND r.accountcode = e.accountcode AND r.standardBriefid = e.standardBriefid AND r.CostCenter = e.BudgetCode
	AND    e.ExecuteDate BETWEEN r.validFrom AND r.validTo
    WHERE  r.companyCode = #{companyCode}
    AND    r.FiscalYear = #{fiscalYear} 
    AND    r.CostCenter = #{budgetCode}
    AND    r.accountcode= #{accountCode}

	AND	   r.standardBriefID = #{standardBriefID}

    AND    STR_TO_DATE(#{executeDate},'%Y%m%d') BETWEEN r.ValidFrom and r.ValidTo

mapper.insert:
	INSERT INTO covi_budget4j.bem_execute_regist
	(
		FiscalYear
		,CompanyCode
		,CostCenter
		,AccountCode
		,StandardBriefID 
		,ExecuteDate
		,UsedAmount
		,Description
		,InitiatorID
		,InitiatorName
		,InitiatorDeptCode
		,InitiatorDeptName
		,ForminstID
		,ExpenceApplicationListID
		,ExpenceApplicationDivID
		,Status
		,BudgetCode
		,ReservedStr1
		,ReservedStr2
		,ReservedStr3 
		,ReservedStr4 
		,ReservedStr5
		,ReservedInt1
		,ReservedInt2 
		,RegisterID
		,RegistDate
	)
     VALUES
	 (


		 		#{fiscalYear}




				 		(SELECT MAX(FiscalYear) FROM covi_budget4j.bem_base_term WHERE STR_TO_DATE(#{executeDate},'%Y%m%d') BETWEEN ValidFrom AND ValidTo)


				 		(SELECT MAX(FiscalYear) FROM covi_budget4j.bem_base_term WHERE NOW(3) BETWEEN ValidFrom AND ValidTo)




	 	,#{companyCode}
		,#{costCenter}
		,#{accountCode}
		,#{standardBriefID}


		 		,#{executeDate}


		 		,NOW(3)


		,#{usedAmount}
		,#{description}
		,#{initiatorID}
		,#{initiatorName}
		,#{initiatorDeptCode}
		,#{initiatorDeptName}
		,#{forminstID}
		,#{expenceApplicationListID}
		,#{expenceApplicationDivID}
		,#{status}
		,#{budgetCode}
		,#{reservedStr1}
		,#{reservedStr2}
		,#{reservedStr3}
		,#{reservedStr4}
		,#{reservedStr5}
		,#{reservedInt1}
		,#{reservedInt2}
		,#{UR_Code}
		, now()
	 )

mapper.update:
UPDATE covi_budget4j.bem_execute_regist
SET    Status = #{status}

	, FiscalYear = (SELECT MAX(FiscalYear) FROM covi_budget4j.bem_base_term WHERE ExecuteDate BETWEEN ValidFrom AND ValidTo)




	    	ExecuteID = #{executeID}


			ExpenceApplicationListID IN (
				SELECT ExpenceApplicationListID 
				FROM covi_account4j_si.act_expence_application_list 
				WHERE ExpenceApplicationID = #{ExpenceApplicationID}
			)
