mapper.select:
        SELECT	FORMAT(IFNULL(NULLIF(SUM(ACB.AmountWon),''),'0'),0)	AS AmountWon
        FROM	covi_account4j_si.act_card_bill	ACB
        WHERE	1=1

		AND		ACB.CompanyCode = #{companyCode}


		AND		SUBSTRING(ACB.ApproveDate,1,6) = #{approveDate}


		AND		ACB.CardUserCode = #{UR_Code}

SELECT ACB.CardBillID , ACB.CompanyCode , covi_account4j_si.Fn_GetBaseCodeName('CompanyCode', ACB.CompanyCode, #{companyCode}) , CONCAT('**********',SUBSTRING(ACB.CardNo,11)) AS CardNo , ACB.ApproveNo , CASE WHEN IFNULL(NULLIF(ACB.ApproveDate,''),'NODATA') = 'NODATA' THEN '' ELSE DATE_FORMAT(ACB.ApproveDate,'%Y.%m.%d') END AS ApproveDate , ACB.StoreName , ACB.ForeignCurrency , FORMAT(ACB.AmountWon,0) AS AmountWon , IFNULL(NULLIF(covi_account4j_si.Fn_GetBaseCodeName('Country', ACB.CountryIndex, #{companyCode}),''),'') AS CountryIndexName FROM covi_account4j_si.act_card_bill ACB WHERE 1=1
AND ACB.CompanyCode = #{companyCode}
AND SUBSTRING(ACB.ApproveDate,1,6) = #{approveDate}
AND ACB.CardNo LIKE CONCAT('%',#{cardNo},'%')
,ACB.CardBillID DESC
,
CompanyCode CardNo ApproveNo ApproveDate StoreName ForeignCurrency AmountWon CountryIndexName
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

SELECT CONCAT('**********',SUBSTRING(ACB.CardNo,11)) AS CardNo , ACB.CompanyCode , covi_account4j_si.Fn_GetBaseCodeName('CompanyCode', ACB.CompanyCode, #{companyCode}) , ACB.ApproveNo , CASE WHEN IFNULL(NULLIF(ACB.ApproveDate,''),'NODATA') = 'NODATA' THEN '' ELSE DATE_FORMAT(ACB.ApproveDate,'%Y.%m.%d') END AS ApproveDate , ACB.StoreName , ACB.ForeignCurrency , FORMAT(ACB.AmountWon,0) AS AmountWon , IFNULL(NULLIF(covi_account4j_si.Fn_GetBaseCodeName('Country', ACB.CountryIndex, #{companyCode}),''),'') AS CountryIndexName FROM covi_account4j_si.act_card_bill ACB WHERE 1=1
AND ACB.CompanyCode = #{companyCode}
AND SUBSTRING(ACB.ApproveDate,1,6) = #{approveDate}
AND ACB.CardNo LIKE CONCAT('%',#{cardNo},'%')
ORDER BY ACB.CardBillID DESC

SELECT	COUNT(*)
FROM	covi_account4j_si.act_card_bill	ACB
WHERE	1=1

AND		ACB.CompanyCode = #{companyCode}


AND		SUBSTRING(ACB.ApproveDate,1,6) = #{approveDate}


AND		ACB.CardNo LIKE CONCAT('%',#{cardNo},'%')

SELECT ACB.CardBillID , ACB.CompanyCode , covi_account4j_si.Fn_GetBaseCodeName('CompanyCode', ACB.CompanyCode, #{companyCode}) , CONCAT('**********',SUBSTRING(ACB.CardNo,11)) AS CardNo , ACB.ApproveNo , CASE WHEN IFNULL(NULLIF(ACB.ApproveDate,''),'NODATA') = 'NODATA' THEN '' ELSE DATE_FORMAT(ACB.ApproveDate,'%Y.%m.%d') END AS ApproveDate , ACB.StoreName , ACB.ForeignCurrency , FORMAT(ACB.AmountWon,0) AS AmountWon , IFNULL(NULLIF(covi_account4j_si.Fn_GetBaseCodeName('Country', ACB.CountryIndex, #{companyCode}),''),'') AS CountryIndexName FROM covi_account4j_si.act_card_bill ACB WHERE 1=1 AND ACB.CardUserCode = #{UR_Code}
AND ACB.CompanyCode = #{companyCode}
AND SUBSTRING(ACB.ApproveDate,1,6) = #{approveDate}
AND ACB.CardNo LIKE CONCAT('%',#{cardNo},'%')
,ACB.CardBillID DESC
,
CompanyCode CardNo ApproveNo ApproveDate StoreName ForeignCurrency AmountWon CountryIndexName
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

SELECT CONCAT('**********',SUBSTRING(ACB.CardNo,11)) AS CardNo , ACB.CompanyCode , covi_account4j_si.Fn_GetBaseCodeName('CompanyCode', ACB.CompanyCode, #{companyCode}) , ACB.ApproveNo , CASE WHEN IFNULL(NULLIF(ACB.ApproveDate,''),'NODATA') = 'NODATA' THEN '' ELSE DATE_FORMAT(ACB.ApproveDate,'%Y.%m.%d') END AS ApproveDate , ACB.StoreName , ACB.ForeignCurrency , FORMAT(ACB.AmountWon,0) AS AmountWon , IFNULL(NULLIF(covi_account4j_si.Fn_GetBaseCodeName('Country', ACB.CountryIndex, #{companyCode}),''),'') AS CountryIndexName FROM covi_account4j_si.act_card_bill ACB WHERE 1=1 AND ACB.CardUserCode = #{UR_Code}
AND ACB.CompanyCode = #{companyCode}
AND SUBSTRING(ACB.ApproveDate,1,6) = #{approveDate}
AND ACB.CardNo LIKE CONCAT('%',#{cardNo},'%')
ORDER BY ACB.CardBillID DESC

SELECT	COUNT(*)
FROM	covi_account4j_si.act_card_bill	ACB
WHERE	1=1
AND		ACB.CardUserCode = #{UR_Code}

AND		ACB.CompanyCode = #{companyCode}


AND		SUBSTRING(ACB.ApproveDate,1,6) = #{approveDate}


AND		ACB.CardNo LIKE CONCAT('%',#{cardNo},'%')

SELECT	COUNT(*)
FROM	covi_account4j_si.act_card_bill	ACB
WHERE	ACB.ApproveNo	= #{approveNo}

