mapper.select:
SELECT CPCD.CorpCardID , CPCD.CompanyCode , covi_account4j_si.Fn_GetBaseCodeName('CompanyCode', CPCD.CompanyCode, #{companyCode}) AS CompanyName , covi_account4j_si.Fn_GetBaseCodeName('CardCompany', CPCD.CardCompany, #{companyCode}) AS CardCompanyName , CONCAT( '**********', SUBSTRING(CPCD.CardNo,11)) AS CardNo , covi_account4j_si.Fn_GetBaseCodeName('CardStatus', CPCD.CardStatus, #{companyCode}) AS CardStatusName , DATE_FORMAT(CPCD.IssueDate,'%Y.%m.%d') AS IssueDate , CPCD.PayDate AS PayDate , CONCAT(SUBSTRING(CPCD.ExpirationDate,1,4),'/',SUBSTRING(CPCD.ExpirationDate,5,6)) AS ExpirationDate , FORMAT(CPCD.LimitAmount,0) AS LimitAmount , USR.DisplayName AS OwnerUserName , USR.EmpNo AS OwnerUserNum , USRB.DeptName AS OwnerUserDept , CPCD.ReleaseYN , CPCD.CardClass FROM covi_account4j_si.act_corp_card CPCD LEFT OUTER JOIN covi_smart4j.sys_object_user USR ON CPCD.OwnerUserCode = USR.UserCode LEFT OUTER JOIN covi_smart4j.sys_object_user_basegroup USRB ON USR.UserCode = USRB.UserCode AND USRB.JobType = 'Origin' WHERE 1=1
AND CPCD.CompanyCode = #{companyCode}
AND CPCD.OwnerUserCode = #{ownerUserCode}
AND CPCD.CardStatus = #{cardStatus}
AND CPCD.CardNo LIKE CONCAT('%',#{cardNo},'%')
AND CPCD.CardClass = #{cardClass}
,CorpCardID DESC
,
CompanyCode CardCompanyName CardNo CardStatusName IssueDate PayDate ExpirationDate LimitAmount OwnerUserNum OwnerUserName OwnerUserDept ReleaseYN
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

    SELECT	COUNT(*)
	FROM	covi_account4j_si.act_corp_card CPCD
	WHERE	1=1

	AND		CPCD.CompanyCode = #{companyCode}


	AND		CPCD.OwnerUserCode = #{ownerUserCode}


	AND		CPCD.CardStatus = #{cardStatus}


	AND		CPCD.CardNo LIKE CONCAT('%',#{cardNo},'%')


	AND		CPCD.CardClass = #{cardClass}

mapper.insert:
    INSERT INTO covi_account4j_si.act_corp_card (
