		,	LimitAmountType
		,	LimitAmount
		,	Note
		,	RegisterID
		,	RegistDate
		,	ModifierID
		,	ModifyDate
    ) VALUES (
			#{companyCode}
		,	#{cardNo}
		,	#{cardCompany}
		,	#{cardClass}
		,	#{cardStatus}
		,	#{ownerUserCode}
		,	#{vendorNo}
		,	#{issueDate}
		,	#{payDate}
		,	#{expirationDate}
		,	#{limitAmountType}
		,	#{limitAmount}
		,	#{note}
		,	#{UR_Code}
		,	NOW(3)
		,	#{UR_Code}
		,	NOW(3)
    )


	    SELECT LAST_INSERT_ID();

mapper.update:
 		UPDATE covi_account4j_si.act_corp_card
 			SET
 				CompanyCode		= #{companyCode}
			,	CardCompany		= #{cardCompany}
			,	CardClass		= #{cardClass}
			,	CardStatus		= #{cardStatus}
			,	OwnerUserCode	= #{ownerUserCode}
			,	VendorNo		= #{vendorNo}
			,	IssueDate		= #{issueDate}
			,	PayDate			= #{payDate}
			,	ExpirationDate	= #{expirationDate}
			,	LimitAmountType	= #{limitAmountType}
			,	LimitAmount		= #{limitAmount}
			,	Note			= #{note}
			,	ModifierID		= #{UR_Code}
			,	ModifyDate		= NOW(3)
 		WHERE	CorpCardID		= #{corpCardID}

 		UPDATE covi_account4j_si.act_corp_card
 			SET
				CardNo			= #{cardNoChgVal}
			,	ModifierID		= #{UR_Code}
			,	ModifyDate		= NOW(3)
 		WHERE	CorpCardID		= #{corpCardID}

mapper.insert:
    INSERT INTO covi_account4j_si.act_corp_card_search_user (
			CorpCardID
		,	OwnerUserCode
		,	RegisterID
		,	RegistDate
    ) VALUES (
			#{corpCardID}
		,	#{ownerUserCode}
		,	#{UR_Code}
		,	NOW(3)
    )

mapper.select:
SELECT CCSU.CorpCardSearchUserID , CCSU.OwnerUserCode , USR.DisplayName AS OwnerUserName , USR.EmpNo AS OwnerUserNum FROM covi_account4j_si.act_corp_card_search_user CCSU LEFT OUTER JOIN covi_smart4j.sys_object_user USR ON CCSU.OwnerUserCode = USR.UserCode WHERE CCSU.CorpCardID = #{corpCardID}
AND CCSU.ownerUserCode = #{ownerUserCode}
ORDER BY CCSU.CorpCardSearchUserID

    SELECT	ACC.CorpCardID
	FROM	covi_account4j_si.act_corp_card	ACC
	WHERE	ACC.CardNo = #{cardNo}

SELECT	COUNT(*)
FROM	covi_account4j_si.act_corp_card_return RET
JOIN	covi_account4j_si.act_corp_card CARD
ON		RET.CorpCardID		=	CARD.CorpCardID
LEFT 
JOIN	covi_smart4j.sys_object_user USR_OWNER
ON		RET.OwnerUserCode	= 	USR_OWNER.UserCode
LEFT 
JOIN	covi_smart4j.sys_object_user USR_RELEASE_APP
ON		RET.ReleaseApprover	= 	USR_RELEASE_APP.UserCode
LEFT 
JOIN	covi_smart4j.sys_object_user USR_RETURN_APP
ON		RET.ReturnApprover	= 	USR_RETURN_APP.UserCode
LEFT 
JOIN	covi_smart4j.sys_object_user USR_RELEASE
ON		RET.ReleaseUserCode	= 	USR_RELEASE.UserCode

WHERE	1=1	


AND		RET.CompanyCode = #{companyCode}


AND		RET.ReleaseUserCode = #{releaseUserCode}


AND		RET.CardStatus = #{cardStatus}


AND		RET.CardNo like concat('%', #{cardNo}, '%')


AND		RET.CardClass = #{cardClass}


AND		DATE_FORMAT(RET.ReleaseDate ,'%Y%m%d') >=  #{releaseSt}


AND		CASE WHEN NULLIF(RET.ReleaseDate,'') IS NULL THEN '00000000' ELSE DATE_FORMAT(RET.ReleaseDate ,'%Y%m%d') END<=  #{releaseEd}


AND	(	RET.ReleaseUserCode		=	#{UR_Code}
	OR	RET.OwnerUserCode		=	#{UR_Code}
	OR	RET.CorpCardID IN (SELECT SEARCH.CorpCardID FROM covi_account4j_si.act_corp_card_search_user SEARCH WHERE RET.CorpCardID = SEARCH.CorpCardID AND SEARCH.OwnerUserCode	=	#{UR_Code})
	)

SELECT RET.CompanyCode , RET.CompanyCode , covi_account4j_si.Fn_GetBaseCodeName('CompanyCode', RET.CompanyCode, #{companyCode}) AS CompanyName , covi_account4j_si.Fn_GetBaseCodeName('CardCompany', RET.CardCompany, #{companyCode}) AS CardCompanyName , CONCAT( '**********', SUBSTRING(RET.CardNo,11)) AS CardNo , RET.CorpCardReturnID , RET.CorpCardID , CARD.OwnerUserCode , USR_OWNER.DisplayName AS OwnerUser_NM , CARD.ReleaseYN , RET.ReleaseReason , RET.DocLink , DATE_FORMAT(RET.ReleaseDate,'%Y.%m.%d %H:%i') AS ReleaseDate , DATE_FORMAT(RET.ReturnDate,'%Y.%m.%d %H:%i') AS ReturnDate , USR_RELEASE_APP.DisplayName AS ReleaseApprover_NM , USR_RETURN_APP.DisplayName AS ReturnApprover_NM , USR_RELEASE.DisplayName AS ReleaseUser_NM
FROM covi_account4j_si.act_corp_card_return RET JOIN covi_account4j_si.act_corp_card CARD ON RET.CorpCardID = CARD.CorpCardID LEFT JOIN covi_smart4j.sys_object_user USR_OWNER ON RET.OwnerUserCode = USR_OWNER.UserCode LEFT JOIN covi_smart4j.sys_object_user USR_RELEASE_APP ON RET.ReleaseApprover = USR_RELEASE_APP.UserCode LEFT JOIN covi_smart4j.sys_object_user USR_RETURN_APP ON RET.ReturnApprover = USR_RETURN_APP.UserCode LEFT JOIN covi_smart4j.sys_object_user USR_RELEASE ON RET.ReleaseUserCode = USR_RELEASE.UserCode
WHERE 1=1
AND RET.CompanyCode = #{companyCode}
AND RET.ReleaseUserCode = #{releaseUserCode}
AND RET.CardStatus = #{cardStatus}
AND RET.CardNo like concat('%', #{cardNo}, '%')
AND RET.CardClass = #{cardClass}
AND DATE_FORMAT(RET.ReleaseDate ,'%Y%m%d') >= #{releaseSt}
AND CASE WHEN NULLIF(RET.ReleaseDate,'') IS NULL THEN '00000000' ELSE DATE_FORMAT(RET.ReleaseDate ,'%Y%m%d') END<= #{releaseEd}
AND ( RET.ReleaseUserCode = #{UR_Code} OR RET.OwnerUserCode = #{UR_Code} OR RET.CorpCardID IN (SELECT SEARCH.CorpCardID FROM covi_account4j_si.act_corp_card_search_user SEARCH WHERE RET.CorpCardID = SEARCH.CorpCardID AND SEARCH.OwnerUserCode = #{UR_Code}) )
,RET.ReleaseDate DESC
,
CardCompanyName CardNo OwnerUser_NM ReleaseDate ReturnDate ReleaseReason ReleaseApprover_NM ReturnApprover_NM ReleaseUser_NM DocLink
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

mapper.update:
 		UPDATE 	covi_account4j_si.act_corp_card_return
		SET		ReturnDate		= 	#{ReturnDate}
			,	ReturnApprover  =	#{UR_Code}
			,	ModifierID      =	#{UR_Code}
			,	ModifyDate      =	now(3)	
		WHERE	CorpCardID		=	#{corpCardID}
		AND		ReturnDate		IS 	NULL

 		UPDATE 	covi_account4j_si.act_corp_card
 		SET 	ReleaseYN		= #{ReleaseYN}
			,	ReleaseUserCode	= CASE WHEN #{ReleaseYN} ='Y' THEN #{ReleaseUserCode} ELSE null END
			,	ReleaseDate		= CASE WHEN #{ReleaseYN} ='Y' THEN #{ReleaseDate} ELSE null END
			,	ReturnDate		= CASE WHEN #{ReleaseYN} ='N' THEN #{ReturnDate} ELSE null END
 		WHERE	CorpCardID		= #{corpCardID}
