mapper.select:
/* accountPortal.selectTaxInvioceList 포탈 세금계산서 목록 */ SELECT TI.TaxInvoiceID AS "TaxInvoiceID" , TI.CompanyCode AS "CompanyCode" , TI.WriteDate AS "WriteDate" , TI.TaxTotal AS "TaxTotal" , TI.SupplyCostTotal AS "SupplyCostTotal" , TI.TotalAmount AS "TotalAmount" , TI.Remark1 AS "Remark1" , TI.InvoicerCorpName AS "InvoicerCorpName" , TO_CHAR(TO_DATE(TI.WriteDate, 'YYYYMMDD'), 'YYYY.MM.DD') AS "WriteDateStr" FROM act_taxinvoice TI WHERE 1=1 AND TI.DataIndex = 'BUY'
AND ( UPPER(TI.InvoiceeEmail1) IN ( SELECT UPPER(AM.TaxMailAddress) FROM act_manager AM WHERE AM.ManagerUserCode = #{UR_Code} UNION SELECT UPPER(SOU.MailAddress) FROM sys_object_user SOU WHERE SOU.UserCode = #{UR_Code} ) OR TI.TossUserCode = #{UR_Code} )
AND ROWNUM <= 5 ORDER BY TI.WriteDate DESC

/* accountPortal.selectTaxInvioceListCnt 포탈 세금계산서 목록 */ SELECT Count(*) FROM act_taxinvoice TI WHERE 1=1
AND ( UPPER(TI.InvoiceeEmail1) IN ( SELECT UPPER(AM.TaxMailAddress) FROM act_manager AM WHERE AM.ManagerUserCode = #{UR_Code} UNION SELECT UPPER(SOU.MailAddress) FROM sys_object_user SOU WHERE SOU.UserCode = #{UR_Code} ) OR TI.TossUserCode = #{UR_Code} )
AND TI.DataIndex = 'BUY'
