mapper.select:
SELECT NVL(SUM(Amount),0) "amount" , COUNT(*) "cnt" FROM act_expence_application AEA INNER JOIN act_expence_application_list AEAL ON AEA.ExpenceApplicationID = AEAL.ExpenceApplicationID INNER JOIN act_expence_application_div AEAD ON AEAL.ExpenceApplicationListID = AEAD.ExpenceApplicationListID LEFT OUTER JOIN sys_object_user_basegroup USR_CUD ON AEA.RegisterID = USR_CUD.UserCode AND USR_CUD.JobType = 'Origin' WHERE AEA.CompanyCode = #{companyCode} AND ProofDate LIKE #{payDate}||'%' AND AEA.ApplicationStatus = 'E'
AND AEAD.CostCenterCode = #{deptCode}
AND AEA.RegisterID = #{UR_Code}
AND USR_CUD.DeptCode IN ( SELECT GroupCode FROM sys_object_group WHERE GroupType = 'Dept' AND GroupPath LIKE '%;' || #{stdCode} || ';%' )

SELECT Code AS "Code" , (SELECT CostCenterName FROM act_cost_center acc WHERE acc.CostCenterCode = Code) AS "Name" , Cnt AS "Cnt" , Amount AS "Amount" FROM ( SELECT NVL(Code, 'Total') Code , COUNT(*) Cnt , SUM(Amount) Amount FROM ( SELECT AEAD.CostCenterCode Code , 1 , AEAD.Amount Amount FROM act_expence_application AEA INNER JOIN act_expence_application_list AEAL ON AEA.ExpenceApplicationID = AEAL.ExpenceApplicationID INNER JOIN act_expence_application_div AEAD ON AEAL.ExpenceApplicationListID = AEAD.ExpenceApplicationListID INNER JOIN act_account C ON AEAD.AccountCode = c.AccountCode LEFT OUTER JOIN sys_object_user_basegroup USR_CUD ON AEA.RegisterID = USR_CUD.UserCode AND USR_CUD.JobType = 'Origin' WHERE AEA.CompanyCode = #{companyCode} AND ProofDate LIKE #{payDate} || '%'
AND AEAD.StandardBriefID = #{accountCode}
AND AEAD.AccountCode = #{accountCode}
AND AEA.RegisterID = #{UR_Code}
AND USR_CUD.DeptCode IN ( SELECT GroupCode FROM sys_object_group WHERE GroupType = 'Dept' AND GroupPath LIKE '%;' || #{stdCode} || ';%' )
AND AEA.ApplicationStatus = 'E' ) A GROUP BY ROLLUP(A.Code) ) AA ORDER BY Amount DESC

WITH MASTER AS (
SELECT #{payYear} || (CASE WHEN LEVEL < 10 THEN ('0' || LEVEL) ELSE TO_CHAR(LEVEL) END) payDate FROM DUAL CONNECT BY LEVEL <= 12
), DETAIL AS ( SELECT A.payDate, B.* FROM ( SELECT (#{payYear} || payDate) AS payDate FROM ( SELECT '01' AS payDate FROM DUAL UNION ALL SELECT '02' FROM DUAL UNION ALL SELECT '03' FROM DUAL UNION ALL SELECT '04' FROM DUAL UNION ALL SELECT '05' FROM DUAL UNION ALL SELECT '06' FROM DUAL UNION ALL SELECT '07' FROM DUAL UNION ALL SELECT '08' FROM DUAL UNION ALL SELECT '09' FROM DUAL UNION ALL SELECT '10' FROM DUAL UNION ALL SELECT '11' FROM DUAL UNION ALL SELECT '12' FROM DUAL ) DT ) A LEFT OUTER JOIN ( SELECT SUBSTR(ProofDate, 1, 6) AS "paydt" , SUM(Amount) AS "AmountSum"
SUM(case when StandardBriefID = #{item} then Amount else 0 end) AS SUM_${item}
SUM(case when AccountCode = #{item} then Amount else 0 end) AS SUM_${item}
FROM act_expence_application AEA INNER JOIN act_expence_application_list AEAL ON AEA.ExpenceApplicationID = AEAL.ExpenceApplicationID INNER JOIN act_expence_application_div AEAD ON AEAL.ExpenceApplicationListID = AEAD.ExpenceApplicationListID LEFT OUTER JOIN sys_object_user_basegroup USR_CUD ON AEA.RegisterID = USR_CUD.UserCode AND USR_CUD.JobType = 'Origin' WHERE AEA.CompanyCode = #{companyCode} AND ProofDate LIKE #{payYear} || '%'
AND AEA.RegisterID = #{UR_Code}
AND USR_CUD.DeptCode IN ( SELECT GroupCode FROM sys_object_group WHERE GroupType = 'Dept' AND GroupPath LIKE '%;' || #{stdCode} || ';%' )
AND AEA.ApplicationStatus = 'E' GROUP BY SUBSTR(ProofDate ,1,6) ) B ON A.payDate = B."paydt" ) SELECT A.payDate AS "payDate" , B.* FROM MASTER A LEFT OUTER JOIN DETAIL B ON ( A.payDate = B."paydt" ) ORDER BY A.payDate ASC

WITH DETAIL AS ( SELECT DT.ExecuteDate , NVL(UsedAmount, 0) UsedAmount , Status FROM ( SELECT (#{payYear} || MON) AS ExecuteDate FROM ( SELECT '01' AS MON FROM DUAL UNION ALL SELECT '02' FROM DUAL UNION ALL SELECT '03' FROM DUAL UNION ALL SELECT '04' FROM DUAL UNION ALL SELECT '05' FROM DUAL UNION ALL SELECT '06' FROM DUAL UNION ALL SELECT '07' FROM DUAL UNION ALL SELECT '08' FROM DUAL UNION ALL SELECT '09' FROM DUAL UNION ALL SELECT '10' FROM DUAL UNION ALL SELECT '11' FROM DUAL UNION ALL SELECT '12' FROM DUAL ) DT )DT LEFT OUTER JOIN ( SELECT TO_CHAR(EXR.ExecuteDate, 'YYYYMM') AS ExecuteYM , EXR.UsedAmount , EXR.Status FROM bem_execute_regist EXR WHERE EXR.CompanyCode = #{companyCode} AND AccountCode = #{accountCode}
#{list}
AND Status != 'R' AND InitiatorID = #{UR_Code} AND InitiatorDeptCode = #{stdCode} AND TO_CHAR(EXR.ExecuteDate, 'YYYY') = #{payYear} ) A ON DT.ExecuteDate = A.ExecuteYM ) SELECT NVL(ExecuteDate,'TOTAL') AS "ExecuteDate" , NVL(SUM(UsedAmount),0) AS "UsedAmount" , SUM( CASE WHEN Status = 'C' THEN UsedAmount ELSE 0 END ) AS "complete" , SUM( CASE WHEN Status = 'P' THEN UsedAmount ELSE 0 END ) AS "pending" FROM DETAIL GROUP BY ROLLUP( ExecuteDate )

SELECT 		NVL( SUM(BudgetAmount),0 ) AS "BudgetAmount"
, BaseTerm AS "BaseTerm"
, FN_GETACTBASECODENAME('BaseTerm', BaseTerm,'ALL') AS "BaseTermName"
FROM 		bem_regist REG
INNER JOIN 	sys_object_user_basegroup USR_CUD ON REG.RegisterID = USR_CUD.UserCode AND USR_CUD.JobType = 'Origin'
WHERE 		REG.CompanyCode = #{companyCode}
AND         REG.FiscalYear = #{payYear}
AND			REG.BaseTerm = 'Year'
AND			REG.IsUse	=	'Y'
AND			REG.AccountCode = #{accountCode}

	 #{list}


AND	USR_CUD.UserCode = #{UR_Code} 


AND	USR_CUD.DeptCode IN (			
		SELECT  GroupCode
		FROM 	sys_object_group
		WHERE 	GroupType = 'Dept'
		AND		GroupPath LIKE '%;' || #{stdCode} || ';%'
) 

GROUP BY BaseTerm

SELECT AEAD.CostCenterCode AS "CostCenterCode" , (SELECT CostCenterName FROM act_cost_center ACC WHERE ACC.CostCenterCode = AEAD.CostCenterCode) AS "CostCenterName" , AEA.RegisterID AS "RegisterID" , SOU.DisplayName AS "RegisterName" , TO_CHAR(TO_DATE(AEAL.ProofDate), 'YYYY.MM.DD') AS "ProofDate" , AEAD.AccountCode AS "AccountCode" , AEAD.AccountName AS "AccountName" , AEAD.StandardBriefID AS "StandardBriefID" , AEAD.StandardBriefName AS "StandardBriefName" , AEAD.UsageComment AS "UsageComment" , AEAD.Amount AS "Amount" FROM act_expence_application AEA INNER JOIN act_expence_application_list AEAL ON AEA.ExpenceApplicationID = AEAL.ExpenceApplicationID INNER JOIN act_expence_application_div AEAD ON AEAL.ExpenceApplicationListID = AEAD.ExpenceApplicationListID LEFT OUTER JOIN sys_object_user SOU ON AEA.RegisterID = SOU.UserCode LEFT OUTER JOIN sys_object_user_basegroup USR_CUD ON AEA.RegisterID = USR_CUD.UserCode AND USR_CUD.JobType = 'Origin' WHERE AEA.CompanyCode = #{CompanyCode} AND AEA.ApplicationStatus = 'E' AND AEAL.ProofDate LIKE #{ProofMonth}||'%'
AND AEAD.CostCenterCode = #{CostCenterCode}
AND USR_CUD.DeptCode IN ( SELECT GroupCode FROM sys_object_group WHERE GroupType = 'Dept' AND GroupPath LIKE '%;'||#{DeptCode}||';%' )
AND AEAD.AccountName = #{AccountName}
AND SOU.DisplayName LIKE '%'||#{RegisterName}||'%'
AND AEAD.AccountCode = #{AccountCode}
AND AEAD.StandardBriefName = #{StandardBriefName}
AND AEAD.StandardBriefID = #{StandardBriefID}
AND AEAD.UsageComment LIKE '%'||#{UsageComment}||'%'
AND AEAD.Amount >= #{SearchAmtSt}
AND AEAD.Amount <= #{SearchAmtEd}
,AEAD.Amount DESC
,
"CostCenterName" "RegisterName" "ProofDate" "AccountName" "StandardBriefName" "UsageComment" "Amount"
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}


		SELECT COUNT(*)
		FROM        act_expence_application AEA
		INNER JOIN  act_expence_application_list AEAL			ON AEA.ExpenceApplicationID = AEAL.ExpenceApplicationID
