SELECT VB.BankName AS "BankName" , VB.BankAccountNo AS "BankAccountNo" , TRUNC(EAL.TotalAmount) AS "TotalAmount" , ( CASE WHEN VB.BankAccountName IS NULL OR VB.BankAccountName = '' THEN (CASE WHEN EAL.VendorName IS NULL OR EAL.VendorName = '' THEN ATI.InvoicerCorpName ELSE EAL.VendorName END) ELSE VB.BankAccountName END ) AS "BankAccountName" FROM act_expence_application_list EAL LEFT OUTER JOIN act_vendor V ON V.VendorNo = EAL.VendorNo LEFT OUTER JOIN ( SELECT VENDORID, MAX(BANKID) AS BankID FROM act_vendor_bank SUB GROUP BY SUB.VENDORID ) VB2 ON VB2.VendorID = V.VendorID LEFT OUTER JOIN act_vendor_bank VB ON VB.VendorID = V.VendorID AND VB.BankID = VB2.BankID LEFT OUTER JOIN act_taxinvoice ATI ON EAL.TaxUID = ATI.TaxInvoiceID WHERE EAL.ExpenceApplicationListID IN
#{item}
ORDER BY ExpenceApplicationListID
