/* account.delay.getCardReceiptDelayList */ SELECT ACR.ReceiptID AS "ReceiptID", ACR.CardUserCode AS "CardUserCode", CASE WHEN ACR.InfoIndex = 'A' THEN CASE WHEN NVL(NULLIF(ACR.ApproveDate,''), 'NODATA') = 'NODATA' THEN '' ELSE TO_CHAR(TO_DATE(ACR.ApproveDate), 'YYYY.MM.DD') END ELSE CASE WHEN NVL(NULLIF(ACR.UseDate, ''), 'NODATA') = 'NODATA' THEN '' ELSE TO_CHAR(TO_DATE(ACR.UseDate), 'YYYY.MM.DD') END END AS "ApproveDate", TO_CHAR(TO_TIMESTAMP(ACR.ApproveTime, 'HH24:MI:SS'), 'HH24:MI:SS') AS "ApproveTime", CASE WHEN (ACR.Class = 'B' OR ACR.InfoIndex = 'C') AND ACR.AmountWon > 0 THEN TO_CHAR((ACR.AmountWon * -1), 'FM999,999,999') ELSE TO_CHAR(ACR.AmountWon, 'FM999,999,999') END AS "AmountWon", ACR.StoreName AS "StoreName", ACR.Active AS "Active" FROM act_card_receipt ACR JOIN act_corp_card ACC ON ACR.CardNo = ACC.CardNo WHERE
((ACR.InfoIndex = 'A' AND ACR.ApproveDate BETWEEN TO_CHAR(TO_DATE(#{accountDelayStartDay}), 'YYYYMMDD') AND TO_CHAR(LAST_DAY(TO_DATE(#{accountDelayStartDay})), 'YYYYMMDD')) OR (ACR.InfoIndex != 'A' AND ACR.UseDate BETWEEN TO_CHAR(TO_DATE(#{accountDelayStartDay}),'YYYYMMDD') AND TO_CHAR(LAST_DAY(TO_DATE(#{accountDelayStartDay})),'YYYYMMDD')))
((ACR.InfoIndex = 'A' AND ACR.ApproveDate BETWEEN TO_CHAR(TO_DATE(#{accountDelayStartDay}), 'YYYYMMDD') AND TO_CHAR(TO_DATE(#{accountDelayEndDay}), 'YYYYMMDD')) OR (ACR.InfoIndex != 'A' AND ACR.UseDate BETWEEN TO_CHAR(TO_DATE(#{accountDelayStartDay}), 'YYYYMMDD') AND TO_CHAR(TO_DATE(#{accountDelayEndDay}), 'YYYYMMDD')))
AND ACC.CompanyCode = #{companyCode}
AND ACR.Active = 'N' ORDER BY ACR.CardUserCode

