SELECT ACI.TaxInvoiceID AS "TaxInvoiceID", TO_CHAR(TO_DATE(ACI.WriteDate),'YYYY.MM.DD') AS "WriteDate", ACI.InvoicerCorpName AS "InvoicerCorpName", ACI.InvoicerContactName AS "InvoicerContactName", ACI.InvoiceeEmail1 AS "InvoiceeEmail1", BASE.DisplayName AS "DisplayName", ACM.ManagerUserCode AS "ManagerUserCode", TO_CHAR(ACI.TotalAmount, 'FM999,999,999') AS "TotalAmount" FROM act_taxinvoice ACI INNER JOIN act_manager ACM ON ACI.InvoiceeEmail1 = acm.TaxMailAddress LEFT OUTER JOIN sys_object_user BASE ON ACM.ManagerUserCode = BASE.UserCode INNER JOIN act_expence_application_list AEAL ON ACI.TaxInvoiceID = AEAL.TaxUID LEFT OUTER JOIN act_expence_application AEA ON AEA.ExpenceApplicationID = AEAL.ExpenceApplicationID WHERE 1 = 1 AND ACI.WriteDate BETWEEN REPLACE(#{accountDelayStartDay}, '-', '') AND REPLACE(#{accountDelayEndDay}, '-', '')
AND ACI.CompanyCode = #{companyCode}
AND ACI.DataIndex = 'BUY' AND (AEA.ApplicationStatus IS NULL OR AEA.ApplicationStatus in ('R', 'C', 'DELETE')) ORDER BY ACM.ManagerUserCode

SELECT * FROM act_card_receipt ACR WHERE 1 = 1 AND Active = 'N' AND CardNo = #{CardNo}
AND CASE WHEN ACR.InfoIndex = 'A THEN ACR.ApproveDate ELSE ACR.UseDate END >= REPLACE(#{approveDateS},'.','')
AND CASE WHEN ACR.InfoIndex = 'A' THEN ACR.ApproveDate ELSE ACR.UseDate END <= REPLACE(#{approveDateE},'.','')
