mapper.select:
SELECT FN_GETACTBASECODENAME('CompanyCode',AC.CompanyCode,#{companyCode}) AS "CompanyName" , AC.AccountCode AS "AccountCode" , AC.AccountName AS "AccountName" , AC.AccountShortName AS "AccountShortName" , FN_GETACTBASECODENAME('AccountClass',AC.AccountClass,#{companyCode}) AS "AccountClassName" , AC.IsUse AS "IsUse" , TO_CHAR(AC.RegistDate, 'YYYY-MM-DD HH24:MI:SS') AS "RegistDate" FROM act_account AC WHERE 1=1
AND AC.CompanyCode = #{companyCode}
AND AC.AccountClass = #{accountClass}
AND AC.IsUse = #{isUse}
AND ( AC.AccountCode LIKE '%' || #{searchStr} || '%' OR AC.AccountName LIKE '%' || #{searchStr} || '%' OR AC.AccountShortName LIKE '%' || #{searchStr} || '%' )
AND AC.AccountCode LIKE '%' || #{searchStr} || '%'
AND AC.AccountName LIKE '%' || #{searchStr} || '%'
AND AC.AccountShortName LIKE '%' || #{searchStr} || '%'
ORDER BY AC.AccountID

    SELECT	COUNT(*)
    FROM	act_account
    WHERE	AccountCode	= #{accountCode}

	AND		CompanyCode = #{companyCode}

    SELECT	COUNT(*)
    FROM	act_account AC
    WHERE	1=1

	AND		AC.CompanyCode = #{companyCode}


	AND		AC.AccountClass = #{accountClass}


	AND		AC.IsUse = #{isUse}





	AND		(	AC.AccountCode		LIKE '%' || #{searchStr} || '%'	OR
				AC.AccountName		LIKE '%' || #{searchStr} || '%'	OR
				AC.AccountShortName	LIKE '%' || #{searchStr} || '%'
			)


	AND		AC.AccountCode		LIKE '%' || #{searchStr} || '%'


	AND		AC.AccountName		LIKE '%' || #{searchStr} || '%'


	AND		AC.AccountShortName	LIKE '%' || #{searchStr} || '%'

mapper.insert:
    INSERT INTO act_account (
			CompanyCode
		,	AccountClass
		,	AccountCode
		,	AccountName
		,	AccountShortName
		,	IsUse
		,	Description
		,	RegisterID
		,	RegistDate
		,	ModifierID
		,	ModifyDate
    ) VALUES (
			#{companyCode}
		,	#{accountClass}
		,	#{accountCode}
		,	#{accountName}
		,	#{accountShortName}
		,	#{isUse}
		,	#{description ,jdbcType=VARCHAR}
		,	#{UR_Code}
		,	SYSDATE
		,	#{UR_Code}
		,	SYSDATE
    )

mapper.update:
 		UPDATE act_account
 			SET	IsUse				= #{isUse}

			,	CompanyCode			= #{companyCode}
			,	AccountClass		= #{accountClass}
			,	AccountName			= #{accountName}
			,	AccountShortName	= #{accountShortName}
 			,	Description			= #{description}

 		WHERE	AccountID	= #{accountID}

mapper.select:
    SELECT AC.AccountID AS "AccountID"
    FROM	act_account AC
    WHERE	AC.AccountCode = #{accountCode}

	AND		CompanyCode = #{companyCode}

