mapper.select:
SELECT A.BizTripRequestID AS "BizTripRequestID", A.BizTripApplicationID AS "BizTripApplicationID", A.RequestTitle AS "RequestTitle", A.ProcessID AS "ProcessID", A.FormInstID AS "FormInstID", A.RequesterDeptName AS "RequesterDeptName", A.RequesterName AS "RequesterName", A.BizTripType AS "BizTripType", FN_GETACTBASECODENAME('BizTripType', A.BizTripType, #{companyCode}) AS "BizTripTypeName", A.ProjectCode AS "ProjectCode", A.ProjectName AS "ProjectName", A.StartDate AS "StartDate", A.EndDate AS "EndDate", A.BusinessAreaType AS "BusinessAreaType", FN_GETACTBASECODENAME('BusinessAreaType', A.BusinessAreaType, #{companyCode}) AS "BusinessAreaTypeName", A.BusinessArea AS "BusinessArea", A.RequestDate AS "RequestDate", A.RequestAmount AS "RequestAmount", A.IsExceptApplication AS "IsExceptApplication", A.ApplicationDate AS "ApplicationDate", A.Amount AS "Amount", A.RequestStatus AS "RequestStatus", FN_GETACTBASECODENAME('ExpenceApplicationStatus', A.RequestStatus, #{companyCode}) AS "RequestStatusName", A.ApplicationStatus AS "ApplicationStatus", FN_GETACTBASECODENAME('ExpenceApplicationStatus', A.ApplicationStatus, #{companyCode}) AS "ApplicationStatusName", A.AppProcessID AS "AppProcessID", A.AppFormInstID AS "AppFormInstID", A.ExpenceApplicationID AS "ExpenceApplicationID", A.CompanyCode AS "CompanyCode", FN_GETACTBASECODENAME('CompanyCode', A.CompanyCode, #{companyCode}) AS "CompanyName" FROM ( SELECT ABR.BizTripRequestID, ABA.BizTripApplicationID, ABR.CompanyCode, ABR.RequestTitle, ABR.ProcessID, (SELECT FormInstID FROM jwf_process P WHERE ABR.ProcessID = P.ProcessID) FormInstID, ABR.RequesterDeptName, ABR.RequesterName, ABR.BizTripType, ABR.ProjectCode, ABR.ProjectName, ABR.StartDate, ABR.EndDate, ABR.BusinessAreaType, ABR.BusinessArea, ABR.RequestDate, ABR.RequestAmount, ABR.IsExceptApplication, ABA.ApplicationDate, ABA.Amount, ABR.RequestStatus, ABA.ApplicationStatus, ABA.ProcessID AS AppProcessID, (SELECT FormInstID FROM jwf_process P WHERE ABA.ProcessID = P.ProcessID) AS AppFormInstID, ABA.ExpenceApplicationID FROM act_biztrip_request ABR LEFT OUTER JOIN act_biztrip_application ABA ON ABR.BizTripRequestID = ABA.BizTripRequestID AND ABA.ApplicationStatus != 'DELETE' WHERE 1=1
AND ABR.CompanyCode = #{companyCode}
AND ABR.RequesterID = #{SessionUser}
AND ABR.RequestTitle LIKE '%'||#{RequestTitle}||'%'
AND ABR.RequesterDeptName LIKE '%'||#{RequesterDeptName}||'%'
AND ABR.RequesterName LIKE '%'||#{RequesterName}||'%'
AND ABR.BizTripType = #{BizTripType}
AND ABR.ProjectCode = #{ProjectCode}
AND ABR.BusinessAreaType = #{BusinessAreaType}
AND ABR.StartDate >= #{StartDate}
AND ABR.EndDate <= #{EndDate}
AND ABR.RequestStatus = #{RequestStatus}
AND ABA.ApplicationStatus = #{ApplicationStatus}
) A
, A.RequestDate DESC
,
"CompanyCode" "RequestTitle" "RequesterDeptName" "RequesterName" "BizTripTypeName" "ProjectName" "ApplicationStatusName" "StartDate" "EndDate" "BusinessAreaTypeName" "BusinessArea" "RequestDate" "RequestAmount" "ApplicationDate" "Amount"
ASC DESC

