mapper.select:
        SELECT	TO_CHAR(NVL(NULLIF(TO_CHAR(SUM(ACB.AmountWon)),''),'0'),'999,999,999,999,999,999,999,999')	AS "AmountWon"
        FROM	act_card_bill	ACB
        WHERE	1=1

		AND		ACB.CompanyCode = #{companyCode}


		AND		SUBSTR(ACB.ApproveDate,1,6) = #{approveDate}


		AND		ACB.CardUserCode = #{UR_Code}

SELECT ('**********'||SUBSTR(ACB.CardNo,11)) AS "CardNo" , ACB.CompanyCode AS "CompanyCode" , FN_GETACTBASECODENAME('CompanyCode', ACB.CompanyCode, #{companyCode}) AS "CompanyName" , ACB.ApproveNo AS "ApproveNo" , CASE WHEN NVL(NULLIF(TO_CHAR(ACB.ApproveDate),''),'NODATA') = 'NODATA' THEN '' ELSE TO_CHAR(TO_DATE(ACB.ApproveDate),'YYYY.MM.DD') END AS "ApproveDate" , ACB.StoreName AS "StoreName" , ACB.ForeignCurrency AS "ForeignCurrency" , TO_CHAR(ACB.AmountWon, '999,999,999,999,999,999,999,999') AS "AmountWon" , NVL(NULLIF(FN_GETACTBASECODENAME('Country', ACB.CountryIndex, #{companyCode}),''),'') AS "CountryIndexName" FROM act_card_bill ACB WHERE 1=1
AND ACB.CompanyCode = #{companyCode}
AND SUBSTR(ACB.ApproveDate,1,6) = #{approveDate}
AND ACB.CardNo LIKE '%' || #{cardNo} || '%'
ORDER BY ACB.CardBillID DESC

SELECT	COUNT(*)
FROM	act_card_bill	ACB
WHERE	1=1

AND		ACB.CompanyCode = #{companyCode}


AND		SUBSTR(ACB.ApproveDate,1,6) = #{approveDate}


AND		ACB.CardNo LIKE '%' || #{cardNo} || '%'

SELECT ACB.CardBillID AS "CardBillID" , ACB.CompanyCode AS "CompanyCode" , FN_GETACTBASECODENAME('CompanyCode', ACB.CompanyCode, #{companyCode}) AS "CompanyName" , ('**********' || SUBSTR(ACB.CardNo,11)) AS CardNo , ACB.ApproveNo AS "ApproveNo" , CASE WHEN NVL(NULLIF(TO_CHAR(ACB.ApproveDate),''),'NODATA') = 'NODATA' THEN '' ELSE TO_CHAR(TO_DATE(ACB.ApproveDate),'YYYY.MM.DD') END AS "ApproveDate" , ACB.StoreName AS "StoreName" , ACB.ForeignCurrency AS "ForeignCurrency" , TO_CHAR(ACB.AmountWon, '999,999,999,999,999,999,999,999') AS "AmountWon" , NVL(NULLIF(FN_GETACTBASECODENAME('Country', ACB.CountryIndex, #{companyCode}),''),'') AS "CountryIndexName" FROM act_card_bill ACB WHERE 1=1 AND ACB.CardUserCode = #{UR_Code}
AND ACB.CompanyCode = #{companyCode}
AND SUBSTR(ACB.ApproveDate,1,6) = #{approveDate}
AND ACB.CardNo LIKE '%' || #{cardNo} || '%'
,ACB.CardBillID DESC
,
"CompanyCode" "CardNo" "ApproveNo" "ApproveDate" "StoreName" "ForeignCurrency" "AmountWon" "CountryIndexName"
ASC DESC

SELECT ('**********'||SUBSTR(ACB.CardNo,11)) AS "CardNo" , ACB.CompanyCode AS "CompanyCode" , FN_GETACTBASECODENAME('CompanyCode', ACB.CompanyCode, #{companyCode}) AS "CompanyName" , ACB.ApproveNo AS "ApproveNo" , CASE WHEN NVL(NULLIF(ACB.ApproveDate,''),'NODATA') = 'NODATA' THEN '' ELSE TO_CHAR(ACB.ApproveDate,'YYYY.MM.DD') END AS "ApproveDate" , ACB.StoreName AS "StoreName" , ACB.ForeignCurrency AS "ForeignCurrency" , TO_CHAR(ACB.AmountWon, '999,999,999,999,999,999,999,999') AS "AmountWon" , NVL(NULLIF(FN_GETACTBASECODENAME('Country', ACB.CountryIndex, #{companyCode}),''),'') AS "CountryIndexName" FROM act_card_bill ACB WHERE 1=1 AND ACB.CardUserCode = #{UR_Code}
AND ACB.CompanyCode = #{companyCode}
AND SUBSTR(ACB.ApproveDate,1,6) = #{approveDate}
AND ACB.CardNo LIKE '%' || #{cardNo} || '%'
ORDER BY ACB.CardBillID DESC

SELECT	COUNT(*)
FROM	act_card_bill	ACB
WHERE	1=1
AND		ACB.CardUserCode = #{UR_Code}

AND		ACB.CompanyCode = #{companyCode}


AND		SUBSTR(ACB.ApproveDate,1,6) = #{approveDate}


AND		ACB.CardNo LIKE '%' || #{cardNo} || '%'

SELECT	COUNT(*)
FROM	act_card_bill	ACB
WHERE	ACB.ApproveNo	= #{approveNo}

