mapper.insert:
    INSERT INTO act_cost_center (
			CompanyCode
		,	CostCenterType
		,	CostCenterCode
		,	CostCenterName
		,	NameCode
		,	UsePeriodStart
		,	UsePeriodFinish
		,	IsPermanent
		,	IsUse
		,	Description
		,	RegisterID
		,	RegistDate
		,	ModifierID
		,	ModifyDate
    ) VALUES (
			#{companyCode}
		,	#{costCenterType}			


		        ,	(	SELECT ('C'||	CASE	WHEN COUNT(B.CostCenterID) > 0
											THEN LPAD(MAX(B.CostCenterID)	+ 1,6,0)
											ELSE LPAD(COUNT(B.CostCenterID)	+ 1,6,0)
									END) AS CostCenterCode
				FROM act_cost_center B)


		        , #{costCenterCode}


		,	#{costCenterName}
		,	#{nameCode}
		,	#{usePeriodStart}
		,	#{usePeriodFinish}
		,	#{isPermanent}
		,	#{isUse}
		,	#{description}
		,	#{UR_Code}
		,	SYSDATE
		,	#{UR_Code}
		,	SYSDATE
    )

mapper.update:
UPDATE act_cost_center
	SET	IsUse			= #{isUse}

	,	Description		= #{description}

WHERE	CostCenterCode	= #{costCenterCode}

mapper.insert:
    INSERT INTO act_user_cost_center (
			CompanyCode
		,	UserCode
		,	CostCenterCode
		,	RegisterID
		,	RegistDate
    ) VALUES (
			#{companyCode}
		,	#{userCode}
		,	#{costCenterCode}
		,	#{UR_Code}
		,	SYSDATE
    )

mapper.update:
	    UPDATE act_user_cost_center
 			SET
 				CompanyCode			= #{companyCode}
 			,	CostCenterCode		= NVL(#{costCenterCode}, ' ')
 		WHERE	UserCostCenterID	= #{userCostCenterID}

UPDATE act_cost_center
SET	
		CostCenterType	= #{costCenterType}
	,	CostCenterName	= #{costCenterName}
	,	NameCode		= #{nameCode}
	,	UsePeriodStart	= #{usePeriodStart}
	,	UsePeriodFinish	= #{usePeriodFinish}
	,	IsPermanent		= #{isPermanent}
	,	IsUse			= #{isUse}
	,	Description		= #{description}
	,	ModifierID		= #{UR_Code}
	,	ModifyDate		= SYSDATE
WHERE	CostCenterID	= #{costCenterID}

mapper.select:
SELECT	COUNT(*)
FROM	act_cost_center
WHERE	CostCenterCode	= #{costCenterCode}

