mapper.select:
SELECT	COST.CostCenterID AS "CostCenterID"
FROM	act_cost_center COST
WHERE	COST.CostCenterCode = #{costCenterCode}

SELECT	COUNT(*)
FROM	sys_object_user
WHERE	UserCode = #{userCode}

SELECT	COUNT(*)
FROM	act_user_cost_center
WHERE	UserCode = #{userCode}

