mapper.select:
/* investigation.selectInvestigationListExcel */ SELECT CD.InvestigationID AS "InvestigationID" , CD.CompanyCode AS "CompanyCode" , FN_GETACTBASECODENAME('CompanyCode', CD.CompanyCode, #{companyCode}) AS "CompanyName" , CD.InvestCodeGroup AS "InvestCodeGroup" , CASE WHEN CD.IsGroup = 'Y' THEN CD.InvestCodeName ELSE GrpCD.InvestCodeName END AS "InvestCodeGroupName" , CD.InvestCode AS "InvestCode" , CD.InvestCodeName AS "InvestCodeName" , CD.IsGroup AS "IsGroup" , CD.IsUse AS "IsUse" , CD.SortKey AS "SortKey" , CD.InvestAmount AS "InvestAmount" , CD.Description AS "Description" , CD.RegisterID AS "RegisterID" , CD.ModifierID AS "ModifierID" , TO_CHAR(CD.ModifyDate, 'YYYY.MM.DD') AS "ModifyDate" , TO_CHAR(CD.RegistDate, 'YYYY.MM.DD') AS "RegistDate" FROM act_investigation CD LEFT OUTER JOIN act_investigation GrpCD ON GrpCd.IsGroup = 'Y' AND GrpCD.InvestCode = Cd.InvestCodeGroup WHERE 1=1
AND CD.CompanyCode = #{companyCode}
AND CD.InvestCodeGroup = #{searchGrp}
AND CD.InvestCodeName LIKE '%' || #{searchText} || '%'
ORDER BY GrpCD.SortKey ,CD.IsGroup DESC ,CD.SortKey ,CD.InvestCode

/* investigation.selectInvestigationListCnt */ SELECT COUNT(CD.InvestigationID) FROM act_investigation CD LEFT OUTER JOIN act_investigation GrpCD ON GrpCd.IsGroup = 'Y' AND GrpCD.InvestCode = Cd.InvestCodeGroup WHERE 1=1
AND CD.CompanyCode = #{companyCode}
AND CD.InvestCodeGroup = #{searchGrp}
AND CD.InvestCodeName LIKE '%' || #{searchText} || '%'

 	    /*
 	    	investigation.selectInvestigationCnt
 	    */
 	    SELECT COUNT(CD.InvestigationID)
		FROM act_investigation CD
		WHERE InvestigationID = #{investigationID}

mapper.update:
/*
investigation.updateInvestInfo
*/
UPDATE act_investigation
SET CompanyCode = #{companyCode}
	, InvestCodeName = #{investCodeName}
	, IsUse = #{isUse}
	, SortKey = #{sortKey}
	, InvestAmoun = #{investAmount}
	, Description = #{description}
	, ModifierID = #{userCode}
	, ModifyDate = SYSDATE
WHERE InvestigationID = #{investigationID}

mapper.delete:
    /*
    investigation.deleteInvestInfo
    */
	DELETE FROM act_investigation
	WHERE InvestigationID = #{investigationID}

mapper.select:
SELECT ExpenceApplicationID AS "ExpenceApplicationID", CompanyCode AS "CompanyCode", CompanyName AS "CompanyName", ApplicationTitle AS "ApplicationTitle", ApplicationStatus AS "ApplicationStatus", ProcessID AS "ProcessID", FormInstID AS "FormInstID", RegisterID AS "RegisterID", UserName AS "UserName", DeptName AS "DeptName", JobLevelName AS "JobLevelName", ProofDate AS "ProofDate", ProofDateStr AS "ProofDateStr", PayDate AS "PayDate", PayDateStr AS "PayDateStr", InvestItem AS "InvestItem", InvestItemName AS "InvestItemName", InvestTarget AS "InvestTarget", InvestTargetName AS "InvestTargetName", TotalAmount AS "TotalAmount" FROM ( SELECT AEA.ExpenceApplicationID, AEA.CompanyCode, FN_GETACTBASECODENAME('CompanyCode', AEA.CompanyCode, #{companyCode}) AS CompanyName, AEA.ApplicationTitle, AEA.ApplicationStatus, AEA.ProcessID, (SELECT FormInstID FROM jwf_process P WHERE P.ProcessID = AEA.ProcessID) AS FormInstID, AEA.RegisterID, SOU.DisplayName AS UserName, SOUB.DeptName, SOUB.JobLevelName, AEAL.ProofDate, TO_CHAR(TO_DATE(AEAL.ProofDate, 'YYYYMMDD'), 'YYYY.MM.DD') AS ProofDateStr, AEAL.PayDate, TO_CHAR(TO_DATE(AEAL.PayDate, 'YYYYMMDD'), 'YYYY.MM.DD') AS PayDateStr, AEAL.ReservedStr1 AS InvestItem, (SELECT InvestCodeName FROM act_investigation AII WHERE AEAL.ReservedStr1 = AII.InvestCodeGroup AND AII.IsGroup = 'Y') AS InvestItemName, AEAL.ReservedStr2 AS InvestTarget, (SELECT InvestCodeName FROM act_investigation AIT WHERE AEAL.ReservedStr1 = AIT.InvestCodeGroup AND AEAL.ReservedStr2 = AIT.InvestCode AND AIT.IsGroup = 'N') AS InvestTargetName, AEAL.TotalAmount FROM act_expence_application AEA INNER JOIN act_expence_application_list AEAL ON AEA.ExpenceApplicationID = AEAL.ExpenceApplicationID INNER JOIN sys_object_user SOU ON AEA.RegisterID = SOU.UserCode INNER JOIN sys_object_user_basegroup SOUB ON SOU.UserCode = SOUB.UserCode AND SOUB.JobType = 'Origin' WHERE 1=1 AND AEA.RequestType = 'INVEST' AND AEA.ApplicationStatus = 'E'
AND AEA.CompanyCode = #{companyCode}
AND AEAL.ProofDate = #{ProofDate}
AND AEAL.PayDate = #{PayDate}
AND SOUB.DeptName LIKE '%' || #{DeptName} || '%'
AND SOU.DisplayName LIKE '%' || #{UserName} || '%'
AND AEAL.ReservedStr1 = #{InvestItem}
AND AEAL.ReservedStr2 = #{InvestTarget}
) A
,ProofDate DESC
,
"CompanyCode" "ApplicationTitle" "ProofDateStr" "PayDateStr" "DeptName" "UserName" "JobLevelName" "TotalAmount"
ASC DESC

SELECT ExpenceApplicationID AS "ExpenceApplicationID", CompanyCode AS "CompanyCode", CompanyName AS "CompanyName", ApplicationTitle AS "ApplicationTitle", ApplicationStatus AS "ApplicationStatus", ProcessID AS "ProcessID", RegisterID AS "RegisterID", UserName AS "UserName", DeptName AS "DeptName", JobLevelName AS "JobLevelName", ProofDate AS "ProofDate", ProofDateStr AS "ProofDateStr", PayDate AS "PayDate", PayDateStr AS "PayDateStr", InvestItem AS "InvestItem", InvestItemName AS "InvestItemName", InvestTarget AS "InvestTarget", InvestTargetName AS "InvestTargetName", TotalAmount AS "TotalAmount" FROM ( SELECT AEA.ExpenceApplicationID, AEA.CompanyCode, FN_GETACTBASECODENAME('CompanyCode', AEA.CompanyCode, #{companyCode}) AS CompanyName, AEA.ApplicationTitle, AEA.ApplicationStatus, AEA.ProcessID, AEA.RegisterID, SOU.DisplayName AS UserName, SOUB.DeptName, SOUB.JobLevelName, AEAL.ProofDate, TO_CHAR(TO_DATE(AEAL.ProofDate, 'YYYYMMDD'), 'YYYY.MM.DD') AS ProofDateStr, AEAL.PayDate, TO_CHAR(TO_DATE(AEAL.PayDate, 'YYYYMMDD'), 'YYYY.MM.DD') AS PayDateStr, AEAL.ReservedStr1 AS InvestItem, (SELECT InvestCodeName FROM act_investigation AII WHERE AEAL.ReservedStr1 = AII.InvestCodeGroup AND AII.IsGroup = 'Y') AS InvestItemName, AEAL.ReservedStr2 AS InvestTarget, (SELECT InvestCodeName FROM act_investigation AIT WHERE AEAL.ReservedStr1 = AIT.InvestCodeGroup AND AEAL.ReservedStr2 = AIT.InvestCode AND AIT.IsGroup = 'N') AS InvestTargetName, AEAL.TotalAmount FROM act_expence_application AEA INNER JOIN act_expence_application_list AEAL ON AEA.ExpenceApplicationID = AEAL.ExpenceApplicationID INNER JOIN sys_object_user SOU ON AEA.RegisterID = SOU.UserCode INNER JOIN sys_object_user_basegroup SOUB ON SOU.UserCode = SOUB.UserCode AND SOUB.JobType = 'Origin' WHERE 1=1 AND AEA.RequestType = 'INVEST' AND AEA.ApplicationStatus = 'E'
AND AEA.CompanyCode = #{companyCode}
AND AEAL.ProofDate = #{ProofDate}
AND AEAL.PayDate = #{PayDate}
AND SOUB.DeptName LIKE '%' || #{DeptName} || '%'
AND SOU.DisplayName LIKE '%' || #{UserName} || '%'
AND AEAL.ReservedStr1 = #{InvestItem}
AND AEAL.ReservedStr2 = #{InvestTarget}
) A
,ProofDate DESC
,
"CompanyCode" "ApplicationTitle" "ProofDateStr" "PayDateStr" "DeptName" "UserName" "JobLevelName" "TotalAmount"
ASC DESC

SELECT COUNT(AEA.ExpenceApplicationID) FROM act_expence_application AEA INNER JOIN act_expence_application_list AEAL ON AEA.ExpenceApplicationID = AEAL.ExpenceApplicationID INNER JOIN sys_object_user SOU ON AEA.RegisterID = SOU.UserCode INNER JOIN sys_object_user_basegroup SOUB ON SOU.UserCode = SOUB.UserCode AND SOUB.JobType = 'Origin' WHERE 1=1 AND AEA.RequestType = 'INVEST' AND AEA.ApplicationStatus = 'E'
AND AEA.CompanyCode = #{companyCode}
AND AEAL.ProofDate = #{ProofDate}
AND AEAL.PayDate = #{PayDate}
AND SOUB.DeptName LIKE '%' || #{DeptName} || '%'
AND SOU.DisplayName LIKE '%' || #{UserName} || '%'
AND AEAL.ReservedStr1 = #{InvestItem}
AND AEAL.ReservedStr2 = #{InvestTarget}
