mapper.select:
SELECT AR.ReceiptID AS "ReceiptID", AR.CompanyCode AS "CompanyCode", FN_GETACTBASECODENAME('CompanyCode', AR.CompanyCode, #{companyCode}) AS "CompanyName", AR.ExpenceMgmtCode AS "ExpenceMgmtCode", AR.UsageText AS "UsageText", TO_CHAR(TO_DATE(AR.PhotoDate, 'YYYY-MM-DD HH24:MI:SS'), 'YYYYMMDD') AS "PhotoDate", TO_CHAR(TO_DATE(AR.PhotoDate, 'YYYY-MM-DD HH24:MI:SS'), 'YYYY.MM.DD HH24:MI') AS "PhotoDateStr", AR.ReceiptType AS "ReceiptType", FN_GETACTBASECODENAME('ReceiptType', AR.ReceiptType, #{companyCode}) AS "ReceiptTypeName", AR.ReceiptFileID AS "ReceiptFileID", AR.Active AS "Active", FN_GETACTBASECODENAME('Active', AR.Active, #{companyCode}) AS "ActiveName", AR.RegisterID AS "RegisterID", FN_BASEGETDICTIONARY_S(#{lang}, SOU.MultiDisplayName) AS "RegisterName", FN_BASEGETDICTIONARY_S(#{lang}, SOUB.MultiDeptName) AS "RegisterDept", AR.RegistDate AS "RegistDate", TO_CHAR(AR.RegistDate, 'YYYY-MM-DD HH24:MI') AS "RegistDateStr", SB.StandardBriefID AS "StandardBriefID", SB.StandardBriefName AS "StandardBriefName" FROM ACT_RECEIPT AR LEFT OUTER JOIN ACT_STANDARD_BRIEF SB ON AR.StandardBriefID = SB.StandardBriefID INNER JOIN SYS_OBJECT_USER SOU ON AR.RegisterID = SOU.UserCode INNER JOIN SYS_OBJECT_USER_BASEGROUP SOUB ON SOU.UserCode = SOUB.UserCode AND SOUB.JobType = 'Origin' WHERE 1=1
AND AR.CompanyCode = #{companyCode}
AND AR.RegisterID = #{userCode}
AND AR.PhotoDate >= #{photoDateS}
AND AR.PhotoDate <= #{photoDateE}
AND AR.Active = #{active}
AND AR.ReceiptType = #{receiptType}
,AR.ReceiptID DESC
,
"CompanyCode" "ActiveName" "PhotoDateStr" "ReceiptTypeName" "UsageText" "RegisterName" "RegisterDept" "StandardBriefName"
ASC DESC

