		,RegID
		,RegDate			
		,FormHelperContext
		,FormNoticeContext			
		,Reserved1			
		,CompanyCode
		,AclAllYN
)
VALUE
(
		#{FormID}
		,#{FormClassID}
		,#{SchemaID}
		,#{IsUse}
		,#{Revision}
		,#{SortKey}
		,#{FormName}
		,#{FormPrefix}
		,#{FormDesc}
		,#{FileName}
		,#{BodyDefault}
		,#{ExtInfo}
		,#{AutoApprovalLine}
		,#{BodyType}
		,#{SubTableInfo}
		,#{RegID}
		,now(3)			
		,#{FormHelperContext}
		,#{FormNoticeContext}
		,#{AuthDept}
		,#{CompanyCode}
		,#{AclAllYN}
)


	    SELECT LAST_INSERT_ID();

mapper.select:
SELECT A.FormClassID AS optionValue , concat('[',B.DisplayName,'] ', A.FormClassName) as optionText FROM COVI_APPROVAL4J.JWF_FormClass A INNER JOIN covi_smart4j.sys_object_domain B ON A.EntCode = B.DomainCode WHERE ( (A.AclAllYN = 'Y'
and A.EntCode IN ('ORGROOT', #{entCode})
) OR exists ( select 1 from covi_approval4j.jwf_forms_acl jfa where jfa.TargetID = A.FormClassID and jfa.ObjectType = 'CLASS' and jfa.CompanyCode = #{entCode} ) ) ORDER BY A.Sortkey

SELECT A.SchemaID AS optionValue, CONCAT('[',B.DisplayName,'] ',A.SchemaName) as optionText , A.SchemaDesc FROM covi_approval4j.jwf_formsschema A inner join covi_smart4j.sys_object_domain B on A.DomainID = B.DomainID
where B.DomainCode = #{entCode}
ORDER BY A.DomainID ASC, A.SchemaName ASC

SELECT DomainID AS DN_ID ,DomainCode AS DN_Code ,DisplayName ,SortKey ,SortPath ,IsUse FROM covi_smart4j.sys_object_domain WHERE IsUse = 'Y'
AND DomainID IN
#{item}

mapper.update:
UPDATE covi_approval4j.jwf_forms
SET	
	FormID             = #{FormID}
	,FormClassID       = #{FormClassID}
	,SchemaID          = #{SchemaID}
	,IsUse             = #{IsUse}
	,Revision          = #{Revision}
	,SortKey           = #{SortKey}
	,FormName          = #{FormName}
	,FormPrefix        = #{FormPrefix}
	,FormDesc          = #{FormDesc}
	,FileName          = #{FileName}
	,BodyDefault       = #{BodyDefault}
	,ExtInfo           = #{ExtInfo}
	,AutoApprovalLine  = #{AutoApprovalLine}
	,BodyType          = #{BodyType}
	,SubTableInfo      = #{SubTableInfo}			
	,ModID             = #{ModID}
	,ModDate           = now(3)

		,FormHelperContext = #{FormHelperContext}


		,FormNoticeContext = #{FormNoticeContext}	

	,Reserved1 = #{AuthDept}
	,AclAllYN = #{AclAllYN}			
WHERE
	FormID = 	#{FormID}

mapper.delete:
DELETE FI
FROM covi_approval4j.jwf_forminstance AS FI
WHERE FI.FormID = #{FormID};

mapper.select:
    SELECT COUNT(*) FROM information_schema.tables
	WHERE TABLE_SCHEMA = 'covi_approval4j'
	AND TABLE_NAME = #{tableName}

    SELECT count(*) FROM covi_approval4j.jwf_forms
	WHERE FormPrefix = #{Formprefix}
	AND Revision = #{Revision}

		AND CompanyCode = #{CompanyCode}

mapper.update:
UPDATE covi_approval4j.jwf_formslegacy
SET IsUse = #{UseOtherLegacyForm}
WHERE FormID = #{FormID}

mapper.delete:
DELETE FROM covi_approval4j.jwf_forms_acl
WHERE ObjectType = 'FORM' AND TargetID = #{FormID}
AND ID NOT IN 

	SELECT ID FROM covi_approval4j.jwf_forms_acl
	WHERE ObjectType = #{item.ObjectType} AND TargetID = #{item.TargetID}
	AND CompanyCode = #{item.CompanyCode} AND GroupCode = #{item.GroupCode}
	AND GroupType = #{item.GroupType}

		UNION ALL

mapper.insert:
INSERT INTO covi_approval4j.jwf_forms_acl
	(ObjectType,TargetID,CompanyCode,GroupCode,GroupType,RegisterCode)

	SELECT
		#{item.ObjectType}
		,#{item.TargetID}
		,#{item.CompanyCode}
		,#{item.GroupCode}
		,#{item.GroupType}
		,#{item.RegisterCode}
	FROM DUAL
	WHERE NOT EXISTS(
		SELECT 1 FROM covi_approval4j.jwf_forms_acl
		WHERE ObjectType = #{item.ObjectType} AND TargetID = #{item.TargetID}
		AND CompanyCode = #{item.CompanyCode} AND GroupCode = #{item.GroupCode}
		AND GroupType = #{item.GroupType}
	)

		UNION ALL

mapper.delete:
DELETE FROM covi_approval4j.jwf_forms_acl
WHERE ObjectType = #{ObjectType}
AND TargetID = #{FormID}
