mapper.select:

SELECT 	COUNT(*) AS cnt
FROM covi_approval4j.jwf_docclass A
WHERE EntCode = #{EntCode}		
	AND ParentDocClassID = #{DocClassID}

mapper.update:

UPDATE covi_approval4j.jwf_docclass
SET 
	ClassName =#{ClassName}
   ,KeepYear = #{KeepYear}
   ,SortKey = #{SortKey}
   ,UpdateDate = now(3)
WHERE
	DocClassID = #{DocClassID}
AND EntCode =#{EntCode}

mapper.delete:

delete FROM covi_approval4j.jwf_docclass		
WHERE
	DocClassID = #{DocClassID}
AND EntCode =#{EntCode}
