mapper.select:
SELECT LegacyConfigID ,SchemaID ,ApvMode ,Seq ,ProgramName ,IfType ,HttpUrl ,SqlClause ,InvokeJavaClassName ,ExtInfo ,HttpBody ,HttpParams ,WSRequestXML ,WSRequestObjectInfo ,ErrorOnFail ,IsUse FROM covi_approval4j.jwf_legacy_if_config WHERE 1=1
AND LegacyConfigID = #{LegacyConfigID}
AND SchemaID = #{SchemaID}
AND ApvMode = #{ApvMode}
AND IsUse = #{IsUse}
ORDER BY ApvMode, Seq

mapper.update:
UPDATE covi_approval4j.jwf_legacy_if_config 
SET ProgramName = #{ProgramName}
	,IfType = #{IfType}
	,HttpUrl = #{HttpUrl}
	,SqlClause = #{SqlClause}
	,InvokeJavaClassName = #{InvokeJavaClassName}
	,ExtInfo = #{ExtInfo}
	,HttpBody = #{HttpBody}
	,HttpParams = #{HttpParams}
	,WSRequestXML = #{WSRequestXML}
	,WSRequestObjectInfo = #{WSRequestObjectInfo}
	,ErrorOnFail = #{ErrorOnFail}
	,ModifyerCode = #{ModifyerCode}
	,ModifyDate = #{ModifyDate}
WHERE LegacyConfigID = #{LegacyConfigID}

UPDATE covi_approval4j.jwf_legacy_if_config
SET IsUse = #{IsUse} 
WHERE LegacyConfigID = #{LegacyConfigID}
