mapper.update:
UPDATE covi_approval4j.jwf_forminstance
FormInstID = #{FormInstID}, ProcessID = #{ProcessID}, SchemaID = #{SchemaID}, InitiatorID = #{InitiatorID}, InitiatorUnitID = #{InitiatorUnitID}, InitiatedDate = #{InitiatedDate}, DeletedDate = #{DeletedDate}, EntName = #{EntName}, DocLevel = #{DocLevel}, DocClassName = #{DocClassName}, AttachFileInfo = #{AttachFileInfo}, SaveTerm = #{SaveTerm}, AppliedTerm = #{AppliedTerm}, ReceiveNames = #{ReceiveNames}, BodyType = #{BodyType}, BodyContext = #{BodyContext}, FormID = #{FormID}, Subject = #{Subject}, InitiatorName = #{InitiatorName}, InitiatorUnitName = #{InitiatorUnitName}, CompletedDate = #{CompletedDate}, EntCode = #{EntCode}, DocNo = #{DocNo}, DocClassID = #{DocClassID}, IsPublic = #{IsPublic}, AppliedDate = #{AppliedDate}, ReceiveNo = #{ReceiveNo}, ReceiptList = #{ReceiptList}, DocLinks = #{DocLinks},
WHERE FormInstID = #{fiid};

UPDATE covi_approval4j.jwf_domaindata AS D
INNER JOIN covi_approval4j.jwf_process AS P ON D.ProcessID = P.ProcessID
SET DomainDataContext = #{DomainDataContext}
WHERE P.FormInstID = #{FormInstID};

UPDATE covi_approval4j.jwf_domaindata AS D
INNER JOIN covi_approval4j.jwf_process AS P ON D.ProcessID = P.ProcessID
SET DomainDataContext = #{DomainDataContext}
WHERE P.FormInstID = #{FormInstID};

UPDATE covi_approval4j.jwf_processdescription
SET IsComment = 'Y'
WHERE FormInstID = #{FormInstID};

mapper.select:
SELECT ApprovalStep FROM covi_approval4j.jwf_processdescription AS PD
INNER JOIN covi_approval4j.jwf_process AS P ON P.ProcessDescriptionID = PD.ProcessDescriptionID
WHERE P.ProcessID = #{ProcessID};

mapper.update:
UPDATE covi_approval4j.jwf_processdescription AS PD INNER JOIN covi_approval4j.jwf_process AS P ON P.ProcessDescriptionID = PD.ProcessDescriptionID
ApprovalStep = #{ApprovalStep}, ApproverName = #{chargeName}, ApproverCode = #{chargeCode},
WHERE P.ProcessID = #{ProcessID};

mapper.select:
select * 
from covi_approval4j.jwf_workitem
where ProcessID = #{processID}
and state != '512' 
ORDER by Created, WorkItemID;
