mapper.select:

SELECT A.*
FROM (
	SELECT A.SortKey,A.UnitName, A.UnitCode, A.EntCode, A.Description,A.AuthStartDate,A.AuthEndDate
		, B.TargetUnitCode AS TargetUnitCode, B.TargetUnitName AS TargetUnitName
		, B.ViewStartDate AS ViewStartDate, B.ViewEndDate AS ViewEndDate
	FROM covi_approval4j.jwf_unitdirector A
	LEFT OUTER JOIN covi_approval4j.jwf_unitdirectormember B ON A.UnitCode = B.UnitCode
	WHERE	A.UnitCode = #{UnitCode}
	AND A.EntCode = #{EntCode}
) A

SELECT A.* FROM ( SELECT A.SortKey ,covi_smart4j.Fn_BaseGetDictionary_S(#{lang}, A.UnitName) AS UnitName , A.UnitCode , A.EntCode , A.Description ,A.AuthStartDate ,A.AuthEndDate , group_concat(B.TargetUnitCode) AS TargetUnitCode , GROUP_CONCAT(CONCAT( covi_smart4j.Fn_BaseGetDictionary_S(#{lang}, B.TargetUnitName), '(', B.TargetUnitCode, ')') ) AS TargetUnitName FROM covi_approval4j.jwf_unitdirector A LEFT OUTER JOIN covi_approval4j.jwf_unitdirectormember B ON A.UnitCode = B.UnitCode
A.UnitCode = #{UnitCode}
A.EntCode = #{EntCode}
GROUP BY A.UnitCode ) A
UnitName TargetUnitName Description SortKey
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}


		SELECT COUNT(*) FROM (SELECT A.SortKey, A.UnitCode, A.EntCode, A.Description  ,A.AuthStartDate ,A.AuthEndDate
		, group_concat(B.TargetUnitCode) AS TargetUnitCode ,  group_concat(B.TargetUnitName) AS TargetUnitName
	FROM covi_approval4j.jwf_unitdirector A LEFT OUTER JOIN covi_approval4j.jwf_unitdirectormember B
ON A.UnitCode = B.UnitCode



		 	A.EntCode = #{EntCode}


		 	AND A.UnitCode = #{UnitCode}


GROUP BY A.UnitCode
	) A

mapper.delete:
      DELETE 
  	FROM covi_approval4j.jwf_unitdirector 
		WHERE   UnitCode = #{UnitCode} 
			AND EntCode = #{EntCode}

mapper.insert:

INSERT INTO covi_approval4j.jwf_unitdirector 
(
	UnitCode
	,EntCode
	,Description
	,SortKey
	,UnitName
	,AuthStartDate
	,AuthEndDate
)
VALUES 
(
	#{UnitCode}
	,#{EntCode}
	,#{Description}
	,IF(#{SortKey} = '', 0, #{SortKey})
	,#{UnitName}
	,IF(#{AuthStartDate} = '', NULL, #{AuthStartDate})
	,IF(#{AuthEndDate} = '', NULL, #{AuthEndDate})
)


INSERT INTO covi_approval4j.jwf_unitdirectormember
(
	UnitCode
	,TargetUnitCode
	,TargetUnitName
	,ViewStartDate
	,ViewEndDate
)
VALUES
(
	#{UnitCode}
	,#{TargetUnitCode}
	,#{TargetUnitName}
	,IF(#{ViewStartDate} = '', NULL, #{ViewStartDate})
	,IF(#{ViewEndDate} = '', NULL, #{ViewEndDate})
)
