mapper.select:
SELECT StorageID , DN_ID , ServiceType , SeverName , LastSeq , FileURL , FilePath , ImageURL , ImagePath , InlineURL , InlinePath , VideoURL , VideoPath , IsUse , Description , RegistDate FROM covi_smart4j.com_storage
AND StorageID = #{storageID}
StorageID DN_ID ServiceType SeverName LastSeq FileURL FilePath ImageURL ImagePath InlineURL InlinePath VideoURL VideoPath IsUse Description RegistDate
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}


		SELECT COUNT(*) 
		FROM covi_smart4j.com_storage


  AND StorageID = #{storageID}

mapper.insert:

INSERT INTO covi_smart4j.com_storage (
	DN_ID
	, ServiceType
	, SeverName
	, LastSeq
	, FileURL
	, FilePath
	, ImageURL
	, ImagePath
	, InlineURL
	, InlinePath
	, VideoURL
	, VideoPath
	, IsUse
	, Description
	, RegistDate
	)
VALUES (		
	  #{dnID}
	, #{serviceType}
	, #{severName}
	, #{lastSeq}
	, #{fileURL}
	, #{filePath}
	, #{imageURL}
	, #{imagePath}
	, #{inlineURL}
	, #{inlinePath}
	, #{videoURL}
	, #{videoPath}
	, #{isUse}
	, #{description}
	, NOW(3)
)

mapper.update:
UPDATE covi_smart4j.com_storage
DN_ID=#{dnID}, ServiceType=#{serviceType}, SeverName=#{severName}, LastSeq=#{lastSeq}, FileURL=#{fileURL}, FilePath=#{filePath}, ImageURL=#{imageURL}, ImagePath=#{imagePath}, InlineURL=#{inlineURL}, InlinePath=#{inlinePath}, VideoURL=#{videoURL}, VideoPath=#{videoPath}, IsUse=#{isUse}, Description=#{description}, RegistDate=#{registDate},
AND StorageID = #{storageID}
