mapper.update:
        UPDATE covi_approval4j.jwf_privatedomaindata
		SET Abstract = #{abstract}, PrivateContext = #{privateContext}
		WHERE PrivateDomainDataID =  #{privateDomainDataID}

mapper.select:
   	SELECT GroupCode AS Code, MultiDisplayName AS Name FROM (
		SELECT gr.GroupCode ,gr.MultiDisplayName FROM covi_approval4j.jwf_groupmember AS mem 
		INNER JOIN covi_smart4j.sys_object_group AS gr ON mem.UserCode = gr.GroupCode
		WHERE mem.GroupID =  #{groupID}
		UNION ALL
		SELECT ur.UserCode, ur.MultiDisplayName FROM covi_approval4j.jwf_groupmember AS mem 
		INNER JOIN covi_smart4j.sys_object_user AS ur ON mem.UserCode = ur.UserCode
		WHERE GroupID = #{groupID}
	) AS Member

 	SELECT GroupID, Type, DisplayName, OwnerID
	FROM covi_approval4j.jwf_privategroup
	WHERE Type='D' and OwnerID = #{userID}

mapper.update:
        UPDATE covi_approval4j.jwf_privatedomaindata
		SET DefaultYN = 'Y'
		WHERE PrivateDomainDataID= #{PrivateDomainID}

       UPDATE covi_approval4j.jwf_privatedomaindata
	   SET DefaultYN = 'N'
	   WHERE OwnerID = #{userID}

mapper.select:
SELECT A.itemType , A.AN , A.DN , A.LV , A.TL , A.PO , A.RG , A.RGNM , A.ETID , A.ETNM , A.EM FROM (
SELECT 'group' AS itemType , GR.GroupCode AS AN , GR.MultiDisplayName AS DN , '' AS LV , '' AS TL , '' AS PO , GR.GroupCode AS RG , GR.MultiDisplayName AS RGNM , CN.CompanyCode AS ETID , CN.MultiDisplayName AS ETNM , GR.PrimaryMail AS EM , GR.SortKey , NULL AS JobTitleSortKey , NULL AS JobLevelSortKey , NULL AS JobTitleCode , NULL AS EnterDate FROM covi_smart4j.sys_object_group AS GR INNER JOIN covi_smart4j.sys_object_group AS CN ON GR.CompanyCode = CN.GroupCode AND CN.GroupType = 'Company' WHERE GR.IsUse = 'Y' AND GR.IsDisplay = 'Y' AND (GR.MemberOf != 'NOUSE' OR GR.MemberOf IS NULL) #향후 그룹의 IsUse나 IsDisplay이를 변경한 경우 해당 그룹에 속한 다른 그룹들도 상태 변경하도록 한다. (운영용) AND (GR.MemberOf != 'NOUNIT' OR GR.MemberOf IS NULL) AND GR.GroupType IN ('Dept','Company') AND (GR.DisplayName LIKE CONCAT('%', #{searchText}, '%') OR GR.PrimaryMail LIKE CONCAT('%', #{searchText}, '%'))
AND GR.CompanyCode = #{companyCode}
UNION
SELECT 'user' AS itemType , UR.UserCode AS AN , UR.MultiDisplayName AS DN , CONCAT(IFNULL(BG.JobLevelCode, ''), ';', IFNULL(BG.MultiJobLevelName, '')) AS LV , CONCAT(IFNULL(BG.JobTitleCode, ''), ';', IFNULL(BG.MultiJobTitleName, '')) AS TL , CONCAT(IFNULL(BG.JobPositionCode, ''), ';', IFNULL(BG.MultiJobPositionName, '')) AS PO , BG.DeptCode AS RG , BG.MultiDeptName AS RGNM , BG.CompanyCode AS ETID , BG.MultiCompanyName AS ETNM , UR.MailAddress AS EM , UR.SortKey , BG.JobTitleSortKey , BG.JobLevelSortKey , BG.JobTitleCode , UR.EnterDate FROM covi_smart4j.sys_object_user_basegroup AS BG INNER JOIN covi_smart4j.sys_object_user AS UR ON UR.UserCode = BG.UserCode WHERE UR.IsUse = 'Y' AND UR.IsDisplay = 'Y' AND (UR.DisplayName LIKE CONCAT('%', #{searchText}, '%') OR BG.JobPositionName LIKE CONCAT('%', #{searchText}, '%') OR BG.DeptName LIKE CONCAT('%', #{searchText}, '%') OR UR.MailAddress LIKE CONCAT('%', #{searchText}, '%'))
AND BG.CompanyCode = #{companyCode}
) A Order BY A.itemType, (A.SortKey+0), A.JobTitleSortKey, A.JobLevelSortKey, A.JobTitleCode, A.EnterDate, A.DN;
