mapper.update:
    UPDATE COVI_APPROVAL4J.JWF_CirculationBox 
    SET DataState = 'D'
    	, ModID = #{ModID}
    	, ModDate = now(3)
    	, DeletedDate = now(3)
	WHERE CirculationBoxID = #{CirculationBoxID};

mapper.select:
    SELECT ParentProcessID
		FROM covi_approval4j.jwf_process
	WHERE ProcessID = #{ProcessID}

    SELECT ParentProcessID
		FROM covi_approval4j.jwf_process 
	WHERE ProcessID = #{ProcessID}


    SELECT Revision,FieldName,ModValue FROM covi_approval4j.jwf_formhistory
	WHERE FormInstID = #{formInstID} AND Revision >= #{revision}
	ORDER BY Revision desc

mapper.insert:
INSERT INTO COVI_APPROVAL4J.jwf_circulationboxdescription ( FormInstID, FormID, FormPrefix, FormName, FormSubject, IsSecureDoc, IsFile, FileExt, IsComment, ApproverCode, ApproverName, ApprovalStep, ApproverSIPAddress, IsReserved, ReservedGubun, Priority, IsModify, Reserved1, Reserved2 )
VALUES ( #{FormInstID}, #{FormID}, #{FormPrefix}, #{FormName}, #{FormSubject}, #{IsSecureDoc}, #{IsFile}, #{FileExt}, #{IsComment}, #{ApproverCode}, #{ApproverName}, #{ApprovalStep}, #{ApproverSIPAddress}, #{IsReserved}, #{ReservedGubun}, #{Priority}, #{IsModify}, #{Reserved1}, #{Reserved2} );
SELECT LAST_INSERT_ID();

mapper.update:
