    SET DataState = 'D'
    	, ModID = #{ModID}
    	, ModDate = now(3)
    	, DeletedDate = now(3)
	WHERE CirculationBoxID = #{CirculationBoxID};

mapper.insert:
INSERT INTO COVI_APPROVAL4J_STORE.jwf_circulationboxdescription ( FormInstID, FormID, FormPrefix, FormName, FormSubject, IsSecureDoc, IsFile, FileExt, IsComment, ApproverCode, ApproverName, ApprovalStep, ApproverSIPAddress, IsReserved, ReservedGubun, Priority, IsModify, Reserved1, Reserved2 )
VALUES ( #{FormInstID}, #{FormID}, #{FormPrefix}, #{FormName}, #{FormSubject}, #{IsSecureDoc}, #{IsFile}, #{FileExt}, #{IsComment}, #{ApproverCode}, #{ApproverName}, #{ApprovalStep}, #{ApproverSIPAddress}, #{IsReserved}, #{ReservedGubun}, #{Priority}, #{IsModify}, #{Reserved1}, #{Reserved2} );
SELECT LAST_INSERT_ID();

mapper.select:

	SELECT COUNT(*) AS CNT
    FROM covi_approval4j.jwf_circulationbox
    WHERE FormInstID = #{FormInstID}
    AND ReceiptID = #{ReceiptID}
    AND Kind = #{Kind}
    AND DataState <> 'D'
	AND DeletedDate is null;


	SELECT COUNT(*) AS CNT
    FROM covi_approval4j_store.jwf_circulationbox
    WHERE FormInstID = #{FormInstID}
    AND ReceiptID = #{ReceiptID}
    AND Kind = #{Kind}
    AND DataState <> 'D'
	AND DeletedDate is null;
