mapper.select:
SELECT A.ProcessID, A.FormInstID, B.FormName, A.Subject, A.InitiatorID FROM covi_approval4j.jwf_forminstance A LEFT JOIN covi_approval4j.jwf_forms B ON A.FormID = B.FormID WHERE 1 = 1
AND A.FormInstID = #{formInstId}
AND A.ProcessID = #{processId}

mapper.insert:

	INSERT 
	  INTO covi_approval4j.jwf_timelinemessaging (ServiceType, MessageType, ObjectID, FormSubject, Subject, 
	  											  Context, GotoURL, RecipientCode, RegistDate, RegisterCode
	  											 )
    VALUES (#{serviceType}, #{messageType}, #{objectId}, #{formSubject}, #{subject}, 
    		#{context}, #{gotoUrl}, #{recipientCode}, now(3), #{registerCode}
    	   )

mapper.select:
	SELECT GROUP_CONCAT(A.substrRegDate SEPARATOR ',') AS dates
	  FROM (SELECT DATE_FORMAT(RegistDate,'%Y/%c/%e') AS substrRegDate FROM (
		  		SELECT Fn_ComURTimeZoneTrans_S(#{userId}, RegistDate) AS RegistDate
				  FROM covi_approval4j.jwf_timelinemessaging A
		 		INNER JOIN covi_approval4j.jwf_process B
        		ON A.ObjectID = B.forminstid and B.parentprocessid = 0
				INNER JOIN covi_approval4j.jwf_processdescription E
				ON B.ProcessDescriptionID = E.ProcessDescriptionID
				 WHERE RecipientCode = #{userId}

					AND RegistDate > DATE_ADD(now(3), INTERVAL - CAST(#{term} AS INTEGER) MONTH)

				 AND A.DeleteDate IS NULL
                 AND B.DeleteDate IS NULL


						AND (E.BusinessData1 IS NULL

								OR E.BusinessData1 IN 

									#{item}


						)


						AND E.BusinessData1 = #{businessData1}


                 ORDER BY RegistDate DESC

					LIMIT #{count}

			   	 ) B
				 GROUP BY DATE_FORMAT(RegistDate, "%Y-%m-%d")
				 ORDER BY RegistDate DESC
		   ) A

SELECT MessagingID, MessageType, ObjectID, A.FormSubject, D.FormPrefix, A.Subject, Context, GotoURL, RegistDate, LOWER(Fn_ComURTimeZoneTrans_S(#{userId}, RegistDate)) AS viewRegDate, DATE_FORMAT(Fn_ComURTimeZoneTrans_S(#{userId}, RegistDate),'%Y/%c/%e') AS substrRegDate, B.ProcessState AS State , E.BusinessData1, E.BusinessData2, F.TaskID , D.FormID FROM covi_approval4j.jwf_timelinemessaging A INNER JOIN covi_approval4j.jwf_process B ON A.ObjectID = B.forminstid and B.parentprocessid = 0 INNER JOIN covi_approval4j.jwf_forminstance C ON B.forminstid = C.forminstid INNER JOIN covi_approval4j.jwf_forms D ON C.formid = D.formid INNER JOIN covi_approval4j.jwf_processdescription E ON B.ProcessDescriptionID = E.ProcessDescriptionID LEFT OUTER JOIN covi_approval4j.jwf_workitem F ON B.processid = F.processid AND F.UserCode = A.RecipientCode AND F.state = '288' WHERE RecipientCode = #{userId}
AND RegistDate > DATE_ADD(now(3), INTERVAL - CAST(#{term} AS INTEGER) MONTH)
AND A.DeleteDate IS NULL AND B.DeleteDate IS NULL
AND (E.BusinessData1 IS NULL
OR E.BusinessData1 IN
#{item}
)
AND E.BusinessData1 = #{businessData1}
ORDER BY RegistDate DESC
LIMIT #{count}

        SELECT UserCode AS UR_Code, MultiDisplayName AS UR_Name
		FROM covi_smart4j.sys_object_user
		WHERE UserCode = #{userId}
