mapper.select:
SELECT * FROM ( SELECT a.LegacyID, a.FormInstID, a.Parameters, a.Mode, a.State, a.EventTime, a.ErrorMessage, a.ErrorStackTrace, a.ErrorClass, a.DeleteTime, IFNULL(a.ProcessID,(SELECT f.ProcessID FROM covi_approval4j.jwf_forminstance f WHERE a.FormInstID = f.FormInstID AND f.FormInstID IS NOT null)) ProcessID, a.ApvMode, a.ApproverId, a.DocNumber, b.Subject, b.InitiatorName, c.FormName, (SELECT p.ProcessState FROM covi_approval4j.jwf_process p WHERE p.FormInstID = a.FormInstID AND p.ProcessID = b.ProcessID) ProcessState, (SELECT BusinessData1 FROM covi_approval4j.jwf_processdescription WHERE FormInstID = a.FormInstID LIMIT 1) BusinessData1, (SELECT BusinessData2 FROM covi_approval4j.jwf_processdescription WHERE FormInstID = a.FormInstID LIMIT 1) BusinessData2, a.LastRetryTime FROM covi_approval4j.jwf_legacy a LEFT OUTER JOIN covi_approval4j.jwf_forminstance b ON a.FormInstID = b.FormInstID LEFT OUTER JOIN covi_approval4j.jwf_forms c ON b.FormID = c.FormID WHERE 1=1 AND a.DeleteTime IS NULL AND a.Mode = #{searchMode}
AND a.State = #{searchState}
AND a.EventTime BETWEEN #{startDate} AND TIMESTAMPADD(Day,1,#{endDate})
AND
b.Subject b.InitiatorName c.FormName a.DocNumber a.FormInstID
like CONCAT('%', #{searchWord},'%')
AND ( a.FormInstID = #{icoSearch} OR b.Subject LIKE CONCAT('%',#{icoSearch},'%') OR b.InitiatorName LIKE CONCAT('%',#{icoSearch},'%') OR c.FormName LIKE CONCAT('%',#{icoSearch},'%') OR a.DocNumber LIKE CONCAT('%',#{icoSearch},'%') )
AND EXISTS ( select 'x' from covi_approval4j.jwf_forminstance fi where fi.forminstid = a.forminstid and fi.entcode = #{EntCode} )
) AS LegacyErrorList
LegacyID FormInstID ProcessID ApvMode DocNumber EventTime FormName Subject InitiatorName LegacyID
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

select BG.DeptCode AS DeptID , BG.DeptName , CONCAT(BG.JobLevelCode, ';', BG.JobLevelName) AS JobLevel , CONCAT(BG.JobTitleCode, ';', BG.JobTitleName) AS JobTitle , CONCAT(BG.JobPositionCode, ';', BG.JobPositionName) AS JobPosition from covi_smart4j.sys_object_user U join covi_smart4j.sys_object_user_basegroup BG on U.UserCode = BG.UserCode where U.UserCode = #{urCode}
AND BG.DeptCode = #{oucode}
AND JobType = 'Origin'

mapper.update:
update covi_approval4j.jwf_legacy
set DeleteTime = now(3)
where Legacyid = #{legacyID}

update covi_approval4j.jwf_legacy
set LastRetryTime = now(3)
where Legacyid = #{LegacyID}

mapper.select:
SELECT * FROM ( SELECT ih.LegacyHistoryID, ih.ApvMode, ih.IfType, ih.LegacyInfo, ih.Parameters, ih.ActionValue, ih.LegacyKey, ih.State, ih.ErrorStackTrace, ih.RawResponse, ih.ResultCode, ih.ResultMessage, ih.FormPrefix, ih.FormInstID, IFNULL(ih.ProcessID,b.ProcessID) ProcessID, ih.UserCode, ih.Subject, ih.EventStartTime, ih.EventEndTime, ih.ElapsedTime, ih.LegacyConfigID, ih.RegistDate, ih.DeleteDate, ih.LastRetryTime, ic.Seq, ic.ProgramName, b.DocNo, b.InitiatorName, c.FormName, p.ProcessState, pd.BusinessData1, pd.BusinessData2 FROM covi_approval4j.jwf_legacy_if_history ih LEFT OUTER join covi_approval4j.jwf_legacy_if_config ic on ih.LegacyConfigID = ic.LegacyConfigID JOIN covi_approval4j.jwf_forminstance b ON ih.FormInstID = b.FormInstID
AND b.EntCode = #{EntCode}
LEFT OUTER JOIN covi_approval4j.jwf_forms c ON b.FormID = c.FormID LEFT OUTER JOIN covi_approval4j.jwf_process p ON p.ProcessID = b.ProcessID LEFT OUTER join covi_approval4j.jwf_processdescription pd on pd.ProcessDescriptionID = p.ProcessDescriptionID WHERE 1=1 AND ih.DeleteDate IS NULL
AND ih.State = #{searchState}
AND ih.RegistDate BETWEEN #{startDate} AND TIMESTAMPADD(Day,1,#{endDate})
AND
ih.ApvMode ic.ProgramName c.FormName ih.Subject b.InitiatorName b.DocNo ih.forminstid
like CONCAT('%', #{searchWord},'%')
AND ( ic.ProgramName = #{icoSearch} OR c.FormName LIKE CONCAT('%',#{icoSearch},'%') OR ih.Subject LIKE CONCAT('%',#{icoSearch},'%') OR b.InitiatorName LIKE CONCAT('%',#{icoSearch},'%') OR b.DocNo LIKE CONCAT('%',#{icoSearch},'%') OR ih.forminstid LIKE CONCAT('%',#{icoSearch},'%') )
) AS LegacyErrorList
ApvMode Seq IfType ProgramName FormInstID ProcessID FormName Subject InitiatorName DocNo RegistDate State LegacyHistoryID
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

mapper.update:
update covi_approval4j.jwf_legacy_if_history
set DeleteDate = now(3)
where LegacyHistoryID = #{legacyHistoryID}
