mapper.select:
SELECT * FROM( SELECT F.StoredFormID, F.StoredFormRevID, C.CategoryID, F.FormPrefix, covi_smart4j.Fn_BaseGetDictionary_S (#{lang}, C.CategoryName) AS CategoryName, covi_smart4j.Fn_BaseGetDictionary_S (#{lang}, F.FormName) AS FormName, R.ThumbnailFileID, IFNULL(F.ModifyDate, F.RegistDate) AS ModifyDate, F.PurchasedCnt, I.IsFree, CASE WHEN P.StoredFormRevID IS NULL THEN 'N' ELSE 'Y' END AS PurchaseYN FROM covi_approval4j.jwf_store_forms F INNER JOIN covi_approval4j.jwf_store_formscategory C ON F.CategoryID = C.CategoryID INNER JOIN covi_smart4j.sys_store_items I ON F.StoreItemID =I.StoreItemID INNER JOIN covi_approval4j.jwf_store_forms_rev R ON R.StoredFormRevID = F.StoredFormRevID LEFT OUTER JOIN ( SELECT B.StoredFormID, A.StoredFormRevID, A.CompanyCode FROM covi_approval4j.jwf_store_purchase A INNER JOIN covi_approval4j.jwf_store_forms_rev B ON A.StoredFormRevID = B.StoredFormRevID WHERE A.CompanyCode = #{CompanyCode} ) P ON F.StoredFormID = P.StoredFormID WHERE F.IsUse = 'Y' AND C.IseUse = 'Y' ) A WHERE 1 = 1
AND A.PurchaseYN = #{filterPurchase}
AND A.IsFree = #{filterIsFree}
AND A.CategoryID = #{filterCategoryID}
AND A.FormName LIKE CONCAT('%', #{searchKeyword}, '%')
AND A.FormPrefix LIKE CONCAT('%', #{searchKeyword}, '%')
AND A.CategoryName LIKE CONCAT('%', #{searchKeyword}, '%')
A.FormName ASC
CategoryName FormName ModifyDate PurchasedCnt PurchaseYN IsFree
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

SELECT COUNT(*) FROM ( SELECT * FROM ( SELECT F.StoredFormID, F.StoredFormRevID, C.CategoryID, F.FormPrefix, covi_smart4j.Fn_BaseGetDictionary_S (#{lang}, C.CategoryName) AS CategoryName, covi_smart4j.Fn_BaseGetDictionary_S (#{lang}, F.FormName) AS FormName, R.ThumbnailFileID, IFNULL(F.ModifyDate, F.RegistDate) AS ModifyDate, F.PurchasedCnt, I.IsFree, CASE WHEN P.StoredFormRevID IS NULL THEN 'N' ELSE 'Y' END AS PurchaseYN FROM covi_approval4j.jwf_store_forms F INNER JOIN covi_approval4j.jwf_store_formscategory C ON F.CategoryID = C.CategoryID INNER JOIN covi_smart4j.sys_store_items I ON F.StoreItemID =I.StoreItemID INNER JOIN covi_approval4j.jwf_store_forms_rev R ON R.StoredFormRevID = F.StoredFormRevID LEFT OUTER JOIN ( SELECT B.StoredFormID, A.StoredFormRevID, A.CompanyCode FROM covi_approval4j.jwf_store_purchase A INNER JOIN covi_approval4j.jwf_store_forms_rev B ON A.StoredFormRevID = B.StoredFormRevID WHERE A.CompanyCode = #{CompanyCode} ) P ON F.StoredFormID = P.StoredFormID WHERE F.IsUse = 'Y' AND C.IseUse ='Y' ) A WHERE 1 = 1
AND A.PurchaseYN = #{filterPurchase}
AND A.IsFree = #{filterIsFree}
AND A.CategoryID = #{filterCategoryID}
AND A.FormName LIKE CONCAT('%', #{searchKeyword}, '%')
AND A.FormPrefix LIKE CONCAT('%', #{searchKeyword}, '%')
AND A.CategoryName LIKE CONCAT('%', #{searchKeyword}, '%')
) B

SELECT A.FormClassID AS optionValue, CONCAT('[',B.DisplayName,'] ', A.FormClassName) AS optionText FROM COVI_APPROVAL4J.JWF_FormClass A INNER JOIN covi_smart4j.sys_object_domain B ON A.EntCode = B.DomainCode WHERE ( (A.AclAllYN = 'Y'
and A.EntCode IN ('ORGROOT', #{CompanyCode})
) OR exists ( SELECT 1 FROM covi_approval4j.jwf_forms_acl jfa WHERE jfa.TargetID = A.FormClassID AND jfa.ObjectType = 'CLASS' AND jfa.CompanyCode = #{CompanyCode} ) ) ORDER BY A.Sortkey

mapper.update:

UPDATE covi_approval4j.jwf_store_forms 
	SET PurchasedCnt = PurchasedCnt + 1
WHERE StoredFormID = #{StoredFormID}

mapper.insert:

INSERT INTO covi_approval4j.jwf_forms(			
	FormClassID
	,SchemaID
	,IsUse
	,Revision
	,SortKey
	,FormName
	,FormPrefix
	,FormDesc
	,FileName
	,BodyDefault
	,ExtInfo
	,AutoApprovalLine
	,BodyType
	,SubTableInfo
	,RegID
	,RegDate			
	,FormHelperContext
	,FormNoticeContext			
	,CompanyCode
	,StoredFormID
)
SELECT
	#{StoredFormClass}
	,#{SchemaID}
	,'Y'
	,'0'
	,(SELECT MAX(SortKey) + 1 FROM covi_approval4j.jwf_forms

			WHERE CompanyCode= #{CompanyCode}

	)
	,#{FormName}
	,#{FormPrefix} 
	,#{FormDescription}
	,#{FileName}
	,BodyDefault

		,#{extInfo}
		,ExtInfo

	,AutoApprovalLine
	,BodyType
	,SubTableInfo
	,#{RegisterCode}
	,NOW(3)
	,FormHelperContext
	,FormNoticeContext
	,#{CompanyCode}
	,#{StoredFormID}
FROM covi_approval4j.jwf_forms F
WHERE FormPrefix = CONCAT('CSTF_STANDARD_', #{StoredFormSchema} ,'_FORM')
LIMIT 1;

    SELECT LAST_INSERT_ID();
