mapper.select:
select count(AggAuthId) as "count" from covi_approval4j.jwf_aggregation_forms jaf inner join covi_approval4j.jwf_aggregation_auth jaa on jaf.AggFormId = jaa.AggFormId where ( (jaa.AuthType = 'U' and jaa.AuthTarget = #{UserID}) or (jaa.AuthType = 'D' and jaa.AuthTarget = #{DeptID}) or ( jaa.AuthType = 'E' and (
jaa.AuthTarget = #{entCode}
jaa.AuthTarget IN ('ORGROOT', #{entCode})
) ) ) and DisplayYN = 'Y'
and jaf.FormPrefix = #{FormPreFix}

select jaf.FormPrefix as "formPrefix", jaf.AggFormId as "aggFormId", jf.FormName as "formName" from covi_approval4j.jwf_aggregation_forms jaf inner join covi_approval4j.jwf_forms jf on jf.FormPrefix = jaf.FormPrefix
and jf.CompanyCode = jaf.EntCode
where EXISTS ( SELECT 1 FROM covi_approval4j.jwf_aggregation_auth jaa WHERE jaf.AggFormId = jaa.AggFormId and ( (jaa.AuthType = 'U' and jaa.AuthTarget = #{userCode}) or (jaa.AuthType = 'D' and jaa.AuthTarget = #{groupCode}) or ( jaa.AuthType = 'E' and (
jaa.AuthTarget = #{companyCode}
jaa.AuthTarget IN ('ORGROOT', #{companyCode})
) ) ) )
and jaf.AggFormId = #{aggFormId}
and DisplayYN = 'Y'

select jaf.FormPrefix as "formPrefix", jf.ExtInfo as "extInfo" from covi_approval4j.jwf_aggregation_forms jaf inner join covi_approval4j.jwf_aggregation_auth jaa on jaf.AggFormId = jaa.AggFormId inner join covi_approval4j.jwf_forms jf on jf.FormPrefix = jaf.FormPrefix
and jf.CompanyCode = jaf.EntCode
where jaf.AggFormId = #{aggFormId} and ( (jaa.AuthType = 'U' and jaa.AuthTarget = #{userCode}) or (jaa.AuthType = 'D' and jaa.AuthTarget = #{groupCode}) or ( jaa.AuthType = 'E' and (
jaa.AuthTarget = #{companyCode}
jaa.AuthTarget IN ('ORGROOT', #{companyCode})
) ) ) limit 1

select exists(select 1 from covi_approval4j.jwf_aggregation_forms jaf inner join covi_approval4j.jwf_aggregation_auth jaa on jaf.AggFormId = jaa.AggFormId where jaf.AggFormId = #{aggFormId} and jaf.FormPrefix = #{aggFormPrefix} and ( (jaa.AuthType = 'U' and jaa.AuthTarget = #{userCode}) or (jaa.AuthType = 'D' and jaa.AuthTarget = #{groupCode}) or ( jaa.AuthType = 'E' and (
jaa.AuthTarget = #{companyCode}
jaa.AuthTarget IN ('ORGROOT', #{companyCode})
) ) ))
