mapper.select:
SELECT COUNT(*) AS CNT FROM covi_approval4j.jwf_bizdoc AS A INNER JOIN covi_approval4j.jwf_bizdocform AS B ON B.BizDocID = A.BizDocID INNER JOIN covi_approval4j.jwf_bizdocmember AS C ON C.BizDocID = A.BizDocID WHERE C.UserCode = #{userCode} AND A.IsUse = 'Y' AND A.BizDocType = #{bizDocType}
AND A.EntCode = #{entCode}
AND A.EntCode IN ('ORGROOT', #{entCode})
ORDER BY A.SortKey asc

SELECT A.BizDocID AS BizDocID ,A.BizDocCode AS BizDocCode ,A.BizDocName AS BizDocName ,C.UserCode AS UserCode FROM COVI_APPROVAL4J.JWF_BIZDOC A INNER JOIN COVI_APPROVAL4J.JWF_BIZDOCMEMBER C ON C.BizDocID = A.BizDocID WHERE C.UserCode = #{userCode} AND A.IsUse = 'Y' AND A.BizDocType = #{bizDocType}
AND A.EntCode = #{entCode}
AND A.EntCode IN ('ORGROOT', #{entCode})
ORDER BY A.SortKey ASC
