mapper.select:
SELECT COUNT(*) FROM (SELECT BD.EntCode ,D.DomainId ,BD.BizDocCode ,BD.BizDocID ,BD.BizDocName ,BD.BizDocType ,BD.Description ,BD.SortKey ,TO_CHAR(BD.InsertDate, 'YYYY-MM-DD HH24:MI') AS "InsertDate" ,BD.IsUse ,BDM.UR_Name ,BDF.FormName FROM jwf_bizdoc BD LEFT OUTER JOIN ( SELECT A.BizDocID, LISTAGG(B.DisplayName,',') within group (ORDER BY B.DisplayName) AS UR_Name from jwf_bizdocmember A INNER JOIN sys_object_user B ON A.UserCode = B.UserCode GROUP BY A.BizDocID ) BDM ON BD.BizDocID = BDM.BizDocID LEFT OUTER JOIN ( SELECT BDF.BizDocID,LISTAGG(BDF.FormName,',') within group (ORDER BY BDF.FormName) AS FormName FROM jwf_bizdocform BDF GROUP BY BDF.BizDocID ) BDF ON BD.BizDocID = BDF.BizDocID LEFT OUTER JOIN sys_object_domain D ON D.DomainCode = BD.EntCode
AND
BizDocCode BizDocName
LIKE '%'||#{search}||'%'
AND BD.BizDocType = #{BizDocType}
AND BD.ENTCODE = #{EntCode}
AND ( BizDocCode LIKE '%'||#{icoSearch}||'%' OR BizDocName LIKE '%'||#{icoSearch}||'%' )
) A


SELECT COUNT(*) FROM jwf_bizdoc
WHERE BizDocCode = #{BizDocCode} 
AND EntCode = #{EntCode}

mapper.insert:
INSERT INTO jwf_bizdoc( 
	EntCode
	,BizDocCode 
	,BizDocName
	,BizDocType
	,Description
	,SortKey
	,InsertDate
	,IsUse
)
VALUES
( 
	#{EntCode}


			, #{BizDocCode}


			, 'BIZDOC_CODE_' || jwf_bizdoc_code_seq.nextval		


	,#{BizDocName}
	,#{BizDocType}
	,#{Description}
	,#{SortKey}
	,SYSDATE
	,#{IsUse} 
)

mapper.update:

UPDATE jwf_bizdoc
SET			
	EntCode = #{EntCode}
	,BizDocCode = #{BizDocCode}
	,BizDocName = #{BizDocName}
	,BizDocType = #{BizDocType}
	,Description = #{Description}
	,SortKey = 	#{SortKey}
	,IsUse = #{IsUse}
WHERE BizDocID = #{BizDocID}

UPDATE jwf_bizdocform
SET	SortKey = #{SortKey}
		,FormPrefix = #{FormPrefix}
		,FormName = #{FormName}			
WHERE BizDocFormID = #{BizDocFormID}

UPDATE jwf_bizdocmember
SET SortKey = 	#{SortKey}		
WHERE BizDocMemberID = #{BizDocMemberID}
