mapper.select:
SELECT LegacyConfigID AS "LegacyConfigID" ,SchemaID AS "SchemaID" ,ApvMode AS "ApvMode" ,Seq AS "Seq" ,ProgramName AS "ProgramName" ,IfType AS "IfType" ,HttpUrl AS "HttpUrl" ,SqlClause AS "SqlClause" ,InvokeJavaClassName AS "InvokeJavaClassName" ,ExtInfo AS "ExtInfo" ,HttpBody AS "HttpBody" ,HttpParams AS "HttpParams" ,WSRequestXML AS "WSRequestXML" ,WSRequestObjectInfo AS "WSRequestObjectInfo" ,ErrorOnFail AS "ErrorOnFail" ,IsUse AS "IsUse" FROM gwuser.jwf_legacy_if_config WHERE 1=1
AND LegacyConfigID = #{LegacyConfigID}
AND SchemaID = #{SchemaID}
AND ApvMode = #{ApvMode}
AND IsUse = #{IsUse}
ORDER BY ApvMode, Seq

mapper.update:
UPDATE gwuser.jwf_legacy_if_config 
SET ProgramName = #{ProgramName}
	,IfType = #{IfType}
	,HttpUrl = #{HttpUrl}
	,SqlClause = #{SqlClause}
	,InvokeJavaClassName = #{InvokeJavaClassName}
	,ExtInfo = #{ExtInfo}
	,HttpBody = #{HttpBody}
	,HttpParams = #{HttpParams}
	,WSRequestXML = #{WSRequestXML}
	,WSRequestObjectInfo = #{WSRequestObjectInfo}
	,ErrorOnFail = #{ErrorOnFail}
	,ModifyerCode = #{ModifyerCode}
	,ModifyDate = #{ModifyDate}
WHERE LegacyConfigID = #{LegacyConfigID}

UPDATE gwuser.jwf_legacy_if_config
SET IsUse = #{IsUse} 
WHERE LegacyConfigID = #{LegacyConfigID}
