mapper.update:
UPDATE jwf_forminstance
FormInstID = #{FormInstID}, ProcessID = #{ProcessID}, SchemaID = #{SchemaID}, InitiatorID = #{InitiatorID}, InitiatorUnitID = #{InitiatorUnitID}, InitiatedDate = TO_DATE(#{InitiatedDate}, 'YYYY-MM-DD HH24:MI:SS'), DeletedDate = TO_DATE(#{DeletedDate}, 'YYYY-MM-DD HH24:MI:SS'), EntName = #{EntName}, DocLevel = #{DocLevel}, DocClassName = #{DocClassName}, AttachFileInfo = #{AttachFileInfo}, SaveTerm = #{SaveTerm}, AppliedTerm = #{AppliedTerm}, ReceiveNames = #{ReceiveNames}, BodyType = #{BodyType}, BodyContext = #{BodyContext}, FormID = #{FormID}, Subject = #{Subject}, InitiatorName = #{InitiatorName}, InitiatorUnitName = #{InitiatorUnitName}, CompletedDate = TO_DATE(#{CompletedDate}, 'YYYY-MM-DD HH24:MI:SS'), EntCode = #{EntCode}, DocNo = #{DocNo}, DocClassID = #{DocClassID}, IsPublic = #{IsPublic}, AppliedDate = TO_DATE(#{AppliedDate}, 'YYYY-MM-DD HH24:MI:SS'), ReceiveNo = #{ReceiveNo}, ReceiptList = #{ReceiptList}, DocLinks = #{DocLinks},
WHERE FormInstID = #{fiid}

         UPDATE jwf_domaindata
	     SET DomainDataContext = #{DomainDataContext}
		 WHERE ProcessID IN (
		                        SELECT ProcessID
		                        FROM jwf_process
		                        WHERE FormInstID = #{FormInstID}
		                       )

       UPDATE jwf_domaindata 
	   SET DomainDataContext = #{DomainDataContext}
	   WHERE ProcessID IN (
		                        SELECT ProcessID
		                        FROM jwf_process
		                        WHERE FormInstID = #{FormInstID}
		                       )

UPDATE jwf_processdescription
SET IsComment = 'Y'
WHERE FormInstID = #{FormInstID};
