mapper.select:
SELECT COUNT(*) FROM jwf_stamp WHERE DelYn = 'N'
AND EntCode=#{EntCode}
AND StampName LIKE '%'||#{search}||'%'

mapper.update:
        UPDATE jwf_stamp 
		SET UseYn = 'N'
		WHERE EntCode = #{EntCode}

      UPDATE jwf_stamp
      SET DelYn = 'Y'
       	 ,ModDate = SYSDATE
       	 ,ModID = #{ModID}    	
		WHERE StampID = #{StampID}

UPDATE jwf_stamp SET
FileID = #{FileID},
EntCode = #{EntCode}, EntName = #{EntName}, StampName = #{StampName}, OrderNo = #{OrderNo}, UseYn = #{UseYn}, ModDate = SYSDATE, ModID = #{ModID} WHERE StampID = #{StampID}

    BEGIN
	    UPDATE jwf_stamp
		SET UseYn = 'N'
		WHERE EntCode = #{EntCode}
		AND StampID != #{StampID}
		AND #{UseYn} = 'Y';				

		UPDATE jwf_stamp
		SET UseYn = (CASE WHEN #{UseYn} = 'Y'  THEN 'Y' 	 ELSE 'N'   END)
		WHERE EntCode = #{EntCode}
		AND StampID = #{StampID};
    END;
