mapper.select:
SELECT StorageID AS "StorageID" , DN_ID AS "DN_ID" , ServiceType AS "ServiceType" , SeverName AS "SeverName" , LastSeq AS "LastSeq" , FileURL AS "FileURL" , FilePath AS "FilePath" , ImageURL AS "ImageURL" , ImagePath AS "ImagePath" , InlineURL AS "InlineURL" , InlinePath AS "InlinePath" , VideoURL AS "VideoURL" , VideoPath AS "VideoPath" , IsUse AS "IsUse" , Description AS "Description" , TO_CHAR(RegistDate, 'YYYY-MM-DD HH24:MI:SS') AS "RegistDate" FROM com_storage
AND StorageID = #{storageID}
StorageID DN_ID ServiceType SeverName LastSeq FileURL FilePath ImageURL ImagePath InlineURL InlinePath VideoURL VideoPath IsUse Description RegistDate
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}


		SELECT COUNT(*) 
		FROM com_storage


  AND StorageID = #{storageID}

mapper.insert:

INSERT INTO com_storage (
	DN_ID
	, ServiceType
	, SeverName
	, LastSeq
	, FileURL
	, FilePath
	, ImageURL
	, ImagePath
	, InlineURL
	, InlinePath
	, VideoURL
	, VideoPath
	, IsUse
	, Description
	, RegistDate
	)
VALUES (		
	  #{dnID}
	, #{serviceType}
	, #{severName}
	, #{lastSeq}
	, #{fileURL}
	, #{filePath}
	, #{imageURL}
	, #{imagePath}
	, #{inlineURL}
	, #{inlinePath}
	, #{videoURL}
	, #{videoPath}
	, #{isUse}
	, #{description}
	, NOW(3)
)

mapper.update:
UPDATE com_storage
DN_ID=#{dnID}, ServiceType=#{serviceType}, SeverName=#{severName}, LastSeq=#{lastSeq}, FileURL=#{fileURL}, FilePath=#{filePath}, ImageURL=#{imageURL}, ImagePath=#{imagePath}, InlineURL=#{inlineURL}, InlinePath=#{inlinePath}, VideoURL=#{videoURL}, VideoPath=#{videoPath}, IsUse=#{isUse}, Description=#{description}, RegistDate=#{registDate},
AND StorageID = #{storageID}
