mapper.select:
SELECT COUNT(*) FROM JWF_FormsLegacy FL INNER JOIN JWF_Forms F ON FL.FormID = F.FormID WHERE FL.LegacyForm = #{legacyFormID}
AND ( F.CompanyCode = #{DN_Code} OR F.CompanyCode = 'ORGROOT' )
AND FL.IsUse = 'Y'

        SELECT FormInstID AS "FormInstID"
		FROM jwf_process
		WHERE ProcessID = #{processID}

        SELECT FormInstID AS "FormInstID"
		FROM jwf_process
		WHERE ProcessID = #{processID}

        SELECT BodyContext
		FROM jwf_forminstance
		WHERE FormInstID = #{FormInstID}

        SELECT LogonID AS "LogonID", DeptCode AS "DeptCode"
		FROM sys_object_user a
			inner join sys_object_user_basegroup b
				on a.usercode = b.usercode
		WHERE a.IsUse = 'Y'


                AND b.DeptCode = #{deptId}


				AND b.JobType = 'Origin' 




                AND empno = #{empNo}


             	AND logonId = #{logonId}


             	AND empno = #{empNo} AND logonId = #{logonId}


                AND a.UserCode = #{usercode}

mapper.insert:

INSERT INTO gwuser.jwf_draft_legacy_target (
	SystemCode
	,DatasourceSeq
	,DataTableName
	,DataTableKeyName
	,SubjectKeyName
	,EmpnoKeyName
	,DeptKeyName
	,MultiTableName
	,MultiTableKeyName
	,FormPrefix
	,Description
	,ModifyDate
	,ModifierCode
)
SELECT 
	#{SystemCode}
	,#{DatasourceSeq}
	,#{DataTableName}
	,#{DataTableKeyName}
	,#{SubjectKeyName}
	,#{EmpnoKeyName}
	,#{DeptKeyName}
	,#{MultiTableName}
	,#{MultiTableKeyName}
	,#{FormPrefix}
	,#{Description}
	,SYSDATE
	,#{ModifierCode}
FROM dual

mapper.update:
  	    UPDATE gwuser.jwf_draft_legacy_target
		SET
			SystemCode = #{SystemCode}
			,DatasourceSeq = #{DatasourceSeq}
			,DataTableName = #{DataTableName}
			,DataTableKeyName = #{DataTableKeyName}
			,SubjectKeyName = #{SubjectKeyName}
			,EmpnoKeyName = #{EmpnoKeyName}
			,DeptKeyName = #{DeptKeyName}
			,MultiTableName = #{MultiTableName}
			,MultiTableKeyName = #{MultiTableKeyName}
			,FormPrefix = #{FormPrefix}
			,Description = #{Description}
			,ModifyDate = SYSDATE
			,ModifierCode = #{ModifierCode}
		WHERE LegacyID=#{LegacyID}

mapper.select:
SELECT LegacyID AS "LegacyID" , SystemCode AS "SystemCode" , LEGACYKEY AS "LEGACYKEY" , EMPNO AS "EMPNO" , DEPT_ID AS "DEPT_ID" , FORM_PREFIX AS "FORM_PREFIX" , SUBJECT AS "SUBJECT" , BODY_CONTEXT AS "BODY_CONTEXT" , STATE AS "STATE" , DATA_TYPE AS "DATA_TYPE" , PROCESS_ID AS "PROCESS_ID" FROM gwuser.jwf_draft_legacy_list
AND LegacyKey = #{Key}
AND SystemCode = #{SystemCode}
AND rownum <= 10
ORDER BY LegacyID DESC
