mapper.select:
SELECT DocSubject AS "DocSubject"
FROM jwf_process
WHERE ProcessID = #{ProcessID}

   SELECT LISTAGG(ProcessID, ';') WITHIN GROUP (ORDER BY ProcessID) AS "ProcessIDs"
	FROM jwf_process
	WHERE FormInstID = #{FormInstID}

mapper.update:
    UPDATE jwf_process
	SET ProcessState = #{State}
	WHERE ProcessID = #{ProcessID}
