mapper.select:
SELECT A.ProcessID AS "ProcessID" , A.FormInstID AS "FormInstID" , B.FormName AS "FormName" , A.Subject AS "Subject" , A.InitiatorID AS "InitiatorID" FROM jwf_forminstance A LEFT JOIN jwf_forms B ON A.FormID = B.FormID WHERE 1 = 1
AND A.FormInstID = #{formInstId}
AND A.ProcessID = #{processId}

SELECT LISTAGG(A.substrRegDate, ',') WITHIN GROUP(ORDER BY A.RegistDateOrder DESC) AS "dates" FROM ( SELECT Fn_ComURTimeZoneTrans_S(#{userId}, RegistDate) AS substrRegDate , MAX(RegistDateOrder) AS RegistDateOrder FROM ( SELECT TO_CHAR(RegistDate, 'YYYY/fmMM/DD') AS RegistDate , TO_CHAR(RegistDate, 'YYYY/MM/DD') AS RegistDateOrder FROM jwf_timelinemessaging A INNER JOIN jwf_process B ON A.ObjectID = B.forminstid and B.parentprocessid = 0 INNER JOIN jwf_processdescription E ON B.ProcessDescriptionID = E.ProcessDescriptionID WHERE RecipientCode = #{userId}
AND RegistDate > ADD_MONTHS(SYSDATE, - TO_NUMBER(#{term}))
AND A.DeleteDate IS NULL AND B.DeleteDate IS NULL
AND (E.BusinessData1 IS NULL
OR E.BusinessData1 IN
#{item}
)
AND E.BusinessData1 = #{businessData1}
AND ROWNUM <= #{count}
) GROUP BY RegistDate ORDER BY RegistDate DESC ) A

SELECT MessagingID AS "MessagingID" , MessageType AS "MessageType" , ObjectID AS "ObjectID" , A.FormSubject AS "FormSubject" , D.FormPrefix AS "FormPrefix" , A.Subject AS "Subject" , Context AS "Context" , GotoURL AS "GotoURL" , RegistDate AS "RegistDate" , LOWER(TO_CHAR(RegistDate, 'YYYY.MM.DD HH12:MI AM','NLS_DATE_LANGUAGE = AMERICAN')) AS "viewRegDate" , TO_CHAR(RegistDate, 'YYYY/fmMM/DD') AS "substrRegDate" , B.ProcessState AS "State" , E.BusinessData1 AS "BusinessData1" , E.BusinessData2 AS "BusinessData2" , F.TaskID AS "TaskID" , D.FormID AS "FormID" FROM jwf_timelinemessaging A INNER JOIN jwf_process B ON A.ObjectID = B.forminstid and B.parentprocessid = 0 INNER JOIN jwf_forminstance C ON B.forminstid = C.forminstid INNER JOIN jwf_forms D ON C.formid = D.formid INNER JOIN jwf_processdescription E ON B.ProcessDescriptionID = E.ProcessDescriptionID LEFT OUTER JOIN jwf_workitem F ON B.processid = F.processid AND F.UserCode = A.RecipientCode AND F.state = '288' WHERE RecipientCode = #{userId}
AND RegistDate > ADD_MONTHS(SYSDATE, - TO_NUMBER(#{term}))
AND A.DeleteDate IS NULL AND B.DeleteDate IS NULL
AND (E.BusinessData1 IS NULL
OR E.BusinessData1 IN
#{item}
)
AND E.BusinessData1 = #{businessData1}
AND ROWNUM <= #{count}
ORDER BY RegistDate DESC
