mapper.select:
SELECT COUNT(*) FROM ( SELECT AuditDeptProcessList.* FROM( SELECT A.ProcessID AS ProcessArchiveID , A.DocSubject , A.InitiatorUnitID , A.InitiatorUnitName , A.InitiatorID , A.InitiatorName , D.ProcessDescriptionID AS ProcessDescriptionArchiveID, D.FormInstID,D.FormID,D.FormName , D.FormSubject,D.IsSecureDoc,D.IsFile,D.FileExt,D.IsComment , D.DocNo,D.ApproverCode,D.ApproverName,D.ApprovalStep,D.ApproverSIPAddress , D.IsReserved,D.ReservedGubun,D.ReservedTime,D.Priority,D.IsModify , D.Reserved1,D.Reserved2,D.FormPrefix , B.Finished AS EndDate ,'' as SubKind ,D.BusinessData1, D.BusinessData2, D.BusinessData3, D.BusinessData4, D.BusinessData5, D.BusinessData6, D.BusinessData7, D.BusinessData8, D.BusinessData9, D.BusinessData10 FROM JWF_Process A INNER JOIN JWF_Workitem B ON A.ProcessID = B.ProcessID INNER JOIN JWF_Performer C ON B.WorkItemID = C.WorkItemID INNER JOIN JWF_ProcessDescription D ON A.ProcessDescriptionID = D.ProcessDescriptionID INNER JOIN sys_object_group E ON A.InitiatorUnitID = E.GroupCode WHERE C.SubKind IN ('T000','T004','T005','T006','T008','T009','T011','T016','T017','T018','T019','T020','T023') AND C.State = 1 AND B.State = 528 AND A.ProcessState = 288 AND A.ParentProcessID = 0
AND A.DeleteDate IS NULL
AND ( E.GroupCode = #{deptID}
OR E.GroupPath LIKE #{deptID} || ';%' OR E.GroupPath LIKE '%;' || #{deptID} || ';%'
)
) AuditDeptProcessList
endDate BETWEEN TO_DATE(#{startDate}, 'YYYY-MM-DD HH24:MI:SS') AND TO_DATE(#{endDate}, 'YYYY-MM-DD HH24:MI:SS') + 1
1=0
AND
DocSubject InitiatorName FormName
LIKE '%' || #{searchWord} || '%'
AND endDate BETWEEN TO_DATE(#{searchGroupWord}, 'YYYY-MM-DD HH24:MI:SS') AND TO_DATE(#{searchGroupWord}, 'YYYY-MM-DD HH24:MI:SS') + 1
AND
AuditDeptProcessList.InitiatorID AuditDeptProcessList.InitiatorUnitID AuditDeptProcessList.FormPrefix
= #{searchGroupWord}
) A

SELECT COUNT(*) FROM ( SELECT AuditDeptCompleteList.* FROM( SELECT A.ProcessID AS ProcessArchiveID , A.DocSubject , A.InitiatorUnitID , A.InitiatorUnitName , A.InitiatorID , A.InitiatorName , C.ProcessDescriptionID AS ProcessDescriptionArchiveID, C.FormInstID,C.FormID,A.FormName , C.FormSubject,C.IsSecureDoc,C.IsFile,C.FileExt,C.IsComment , C.DocNo,C.ApproverCode,C.ApproverName,C.ApprovalStep,C.ApproverSIPAddress , C.IsReserved,C.ReservedGubun,C.ReservedTime,C.Priority,C.IsModify , C.Reserved1,C.Reserved2,A.FormPrefix , A.endDate AS EndDate ,'' as SubKind FROM JWF_Process A INNER JOIN sys_object_group B ON A.InitiatorUnitID = B.GroupCode INNER JOIN JWF_ProcessDescription C ON A.ProcessDescriptionID = C.ProcessDescriptionID WHERE A.DeleteDate IS NULL AND A.ProcessState = 528 AND (A.BusinessState LIKE '02_01%' OR A.BusinessState = '03_01_01') AND A.ParentProcessID = 0
AND ( B.GroupCode = #{deptID}
OR B.GroupPath LIKE #{deptID} || ';%' OR B.GroupPath LIKE '%;' || #{deptID} || ';%'
)
) AuditDeptCompleteList
endDate BETWEEN TO_DATE(#{startDate}, 'YYYY-MM-DD HH24:MI:SS') AND TO_DATE(#{endDate}, 'YYYY-MM-DD HH24:MI:SS') + 1
1=0
AND
DocSubject InitiatorName FormName
LIKE '%' || #{searchWord} || '%'
AND endDate BETWEEN TO_DATE(#{searchGroupWord}, 'YYYY-MM-DD HH24:MI:SS') AND TO_DATE(#{searchGroupWord}, 'YYYY-MM-DD HH24:MI:SS') + 1
AND
AuditDeptCompleteList.InitiatorID AuditDeptCompleteList.InitiatorUnitID AuditDeptCompleteList.FormPrefix
= #{searchGroupWord}
) A

SELECT COUNT(*) FROM ( SELECT AuditDeptCompleteList.* FROM( SELECT A.ProcessArchiveID , A.DocSubject , A.InitiatorUnitID , A.InitiatorUnitName , A.InitiatorID , A.InitiatorName , A.FormPrefix , C.* , A.endDate AS EndDate ,'' as SubKind FROM JWF_ProcessArchive A INNER JOIN sys_object_group B ON A.InitiatorUnitID = B.GroupCode INNER JOIN JWF_ProcessDescriptionArchive C ON A.ProcessDescriptionArchiveID = C.ProcessDescriptionArchiveID WHERE A.DeleteDate IS NULL AND (A.BusinessState LIKE '02_01%' OR A.BusinessState = '03_01_01') AND A.ParentProcessID = 0
AND ( B.GroupCode = #{deptID}
OR B.GroupPath LIKE #{deptID} || ';%' OR B.GroupPath LIKE '%;' || #{deptID} || ';%'
)
) AuditDeptCompleteList
endDate BETWEEN TO_DATE(#{startDate}, 'YYYY-MM-DD HH24:MI:SS') AND TO_DATE(#{endDate}, 'YYYY-MM-DD HH24:MI:SS') + 1
1=0
AND
DocSubject InitiatorName FormName
LIKE '%' || #{searchWord} || '%'
AND endDate BETWEEN TO_DATE(#{searchGroupWord}, 'YYYY-MM-DD HH24:MI:SS') AND TO_DATE(#{searchGroupWord}, 'YYYY-MM-DD HH24:MI:SS') + 1
AND
AuditDeptCompleteList.InitiatorID AuditDeptCompleteList.InitiatorUnitID AuditDeptCompleteList.FormPrefix
= #{searchGroupWord}
) A
