mapper.update:

UPDATE jwf_group
SET GroupCode = #{GroupCode}				
	, GroupName  = #{GroupName}
	, Description = #{Description}
	, SortKey = #{SortKey}
	, IsUse = #{IsUse}			
WHERE GroupID = #{GroupID}

mapper.select:
SELECT GroupCode AS "GroupCode" , GroupID AS "GroupID" , GroupName AS "GroupName" , Description AS "Description" , SortKey AS "SortKey" , IsUse AS "IsUse" , TO_CHAR(InsertDate, 'YYYY-MM-DD HH24:MI') AS "InsertDate" , EntCode FROM jwf_group WHERE 1=1
AND GroupID = #{GroupID}
AND GroupCode = #{GroupCode}

mapper.update:

UPDATE jwf_groupmember
SET SortKey = #{SortKey}						
WHERE	GroupMemberID = #{GroupMemberID}
