mapper.select:
SELECT 	COUNT(*) AS "cnt"
FROM jwf_docclass A
WHERE EntCode = #{EntCode}		
AND ParentDocClassID = #{DocClassID}

mapper.update:
UPDATE jwf_docclass
SET ClassName =#{ClassName}
	 , KeepYear = #{KeepYear}
	 , SortKey = #{SortKey}
	 , UpdateDate = SYSDATE
WHERE DocClassID = #{DocClassID}
AND EntCode =#{EntCode}

mapper.delete:
DELETE FROM jwf_docclass		
WHERE DocClassID = #{DocClassID}
AND EntCode =#{EntCode}
