mapper.insert:

INSERT INTO jwf_signimage 
(
UserCode	
,FileName
,IsUse
,SortKey
,InsertDate
,FilePath
,FileID
)
VALUES 	
(
 #{UserCode}
,#{FileName}
,'Y'
,(SELECT NVL(MAX(SortKey),0) FROM jwf_signimage A)
,SYSDATE
,#{FilePath}
,#{FileID}
)

mapper.delete:
      DELETE
      FROM jwf_signimage
		WHERE UserCode = #{UserCode} 
		AND FileName = #{FileName}

mapper.update:
UPDATE jwf_signimage
SET IsUse = #{IsUse}
WHERE UserCode = #{UserCode} AND FileName = #{FileName}

UPDATE jwf_signimage
SET IsUse = 'N'
WHERE UserCode = #{UserCode}
