mapper.select:
SELECT BG.DeptCode AS "DeptID" , BG.DeptName AS "DeptName" , BG.JobLevelCode || ';' || BG.JobLevelName AS "JobLevel" , BG.JobTitleCode || ';' || BG.JobTitleName AS "JobTitle" , BG.JobPositionCode || ';' || BG.JobPositionName AS "JobPosition" FROM sys_object_user U JOIN sys_object_user_basegroup BG on U.UserCode = BG.UserCode WHERE U.UserCode = #{urCode}
AND BG.DeptCode = #{oucode}
AND JobType = 'Origin'

mapper.update:
UPDATE jwf_legacy
SET DeleteTime = TO_CHAR(SYSTIMESTAMP, 'yyyy-mm-dd hh24:mm:ss.FF3')
WHERE Legacyid = #{legacyID}

UPDATE jwf_legacy
SET LastRetryTime =  SYSDATE
WHERE Legacyid = #{LegacyID}

update gwuser.jwf_legacy_if_history
set DeleteDate = SYSDATE
where LegacyHistoryID = #{legacyHistoryID}
