mapper.select:
SELECT StoredFormID AS "StoredFormID", StoredFormRevID AS "StoredFormRevID", CategoryID AS "CategoryID", FormPrefix AS "FormPrefix", CategoryName AS "CategoryName", FormName AS "FormName", ThumbnailFileID AS "ThumbnailFileID", ModifyDate AS "ModifyDate", PurchasedCnt AS "PurchasedCnt", IsFree AS "IsFree", PurchaseYN AS "PurchaseYN" FROM( SELECT F.StoredFormID, F.StoredFormRevID, C.CategoryID, F.FormPrefix, Fn_BaseGetDictionary_S (#{lang}, C.CategoryName) AS CategoryName, Fn_BaseGetDictionary_S (#{lang}, F.FormName) AS FormName, R.ThumbnailFileID, NVL(F.ModifyDate, F.RegistDate) AS ModifyDate, F.PurchasedCnt, I.IsFree, CASE WHEN P.StoredFormRevID IS NULL THEN 'N' ELSE 'Y' END AS PurchaseYN FROM jwf_store_forms F INNER JOIN jwf_store_formscategory C ON F.CategoryID = C.CategoryID INNER JOIN sys_store_items I ON F.StoreItemID =I.StoreItemID INNER JOIN jwf_store_forms_rev R ON R.StoredFormRevID = F.StoredFormRevID LEFT OUTER JOIN ( SELECT SR.StoredFormID, SP.StoredFormRevID, SP.CompanyCode FROM jwf_store_purchase SP INNER JOIN jwf_store_forms_rev SR ON SP.StoredFormRevID = SR.StoredFormRevID WHERE SP.CompanyCode = #{CompanyCode} ) P ON F.StoredFormID = P.StoredFormID WHERE F.IsUse = 'Y' AND C.IseUse = 'Y' ) WHERE 1 = 1
AND PurchaseYN = #{filterPurchase}
AND IsFree = #{filterIsFree}
AND CategoryID = #{filterCategoryID}
AND FormName LIKE '%' || #{searchKeyword} || '%'
AND FormPrefix LIKE '%' || #{searchKeyword} || '%'
AND CategoryName LIKE '%' || #{searchKeyword} || '%'
FormName ASC
CategoryName FormName ModifyDate PurchasedCnt PurchaseYN IsFree
ASC DESC

SELECT COUNT(*) FROM ( SELECT * FROM ( SELECT F.StoredFormID, F.StoredFormRevID, C.CategoryID, F.FormPrefix, Fn_BaseGetDictionary_S (#{lang}, C.CategoryName) AS CategoryName, Fn_BaseGetDictionary_S (#{lang}, F.FormName) AS FormName, R.ThumbnailFileID, NVL(F.ModifyDate, F.RegistDate) AS ModifyDate, F.PurchasedCnt, I.IsFree, CASE WHEN P.StoredFormRevID IS NULL THEN 'N' ELSE 'Y' END AS PurchaseYN FROM jwf_store_forms F INNER JOIN jwf_store_formscategory C ON F.CategoryID = C.CategoryID INNER JOIN sys_store_items I ON F.StoreItemID =I.StoreItemID INNER JOIN jwf_store_forms_rev R ON R.StoredFormRevID = F.StoredFormRevID LEFT OUTER JOIN ( SELECT B.StoredFormID, A.StoredFormRevID, A.CompanyCode FROM jwf_store_purchase A INNER JOIN jwf_store_forms_rev B ON A.StoredFormRevID = B.StoredFormRevID WHERE A.CompanyCode = #{CompanyCode} ) P ON F.StoredFormID = P.StoredFormID WHERE F.IsUse = 'Y' AND C.IseUse ='Y' ) A WHERE 1 = 1
AND A.PurchaseYN = #{filterPurchase}
AND A.IsFree = #{filterIsFree}
AND A.CategoryID = #{filterCategoryID}
AND A.FormName LIKE '%' || #{searchKeyword} || '%'
AND A.FormPrefix LIKE '%' || #{searchKeyword} || '%'
AND A.CategoryName LIKE '%' || #{searchKeyword} || '%'
) B

SELECT A.FormClassID AS "optionValue", '[' || B.DisplayName ||'] ' || A.FormClassName AS "optionText" FROM JWF_FormClass A INNER JOIN sys_object_domain B ON A.EntCode = B.DomainCode WHERE ( (A.AclAllYN = 'Y'
and A.EntCode IN ('ORGROOT', #{CompanyCode})
) OR exists ( SELECT 1 FROM jwf_forms_acl jfa WHERE jfa.TargetID = A.FormClassID AND jfa.ObjectType = 'CLASS' AND jfa.CompanyCode = #{CompanyCode} ) ) ORDER BY A.Sortkey

mapper.update:

UPDATE jwf_store_forms 
	SET PurchasedCnt = PurchasedCnt + 1
WHERE StoredFormID = #{StoredFormID}

mapper.insert:
INSERT INTO jwf_forms( FormClassID ,SchemaID ,IsUse ,Revision ,SortKey ,FormName ,FormPrefix ,FormDesc ,FileName ,BodyDefault ,ExtInfo ,AutoApprovalLine ,BodyType ,SubTableInfo ,RegID ,RegDate ,FormHelperContext ,FormNoticeContext ,CompanyCode ,StoredFormID ) SELECT #{StoredFormClass} ,#{SchemaID} ,'Y' ,'0' ,(SELECT MAX(SortKey) + 1 FROM jwf_forms
WHERE CompanyCode= #{CompanyCode}
) ,#{FormName} ,#{FormPrefix} ,#{FormDescription} ,#{FileName} ,BodyDefault
,#{extInfo} ,ExtInfo
,AutoApprovalLine ,BodyType ,SubTableInfo ,#{RegisterCode} ,SYSDATE ,FormHelperContext ,FormNoticeContext ,#{CompanyCode} ,#{StoredFormID} FROM jwf_forms F WHERE FormPrefix = 'CSTF_STANDARD_' || #{StoredFormSchema} || '_FORM' AND ROWNUM = 1
SELECT JWF_FORMS_SEQ.CURRVAL FROM dual
