mapper.select:
		SELECT COUNT(*) AS CNT
		FROM JWF_BIZDOC A
		INNER JOIN JWF_BIZDOCFORM B ON B.BizDocID = A.BizDocID
		INNER JOIN JWF_BIZDOCMEMBER C ON C.BizDocID = A.BizDocID
		WHERE C.UserCode = #{userCode}
		AND A.IsUse = 'Y'
		AND   A.BizDocType = #{bizDocType}


                AND A.EntCode = #{entCode}


				AND A.EntCode IN ('ORGROOT', #{entCode})


		ORDER BY A.SortKey ASC

SELECT A.BizDocID AS "BizDocID" ,A.BizDocCode AS "BizDocCode" ,A.BizDocName AS "BizDocName" ,C.UserCode AS "UserCode" FROM JWF_BIZDOC A INNER JOIN JWF_BIZDOCMEMBER C ON C.BizDocID = A.BizDocID WHERE C.UserCode = #{userCode} AND A.IsUse = 'Y' AND A.BizDocType = #{bizDocType}
AND A.EntCode = #{entCode}
AND A.EntCode IN ('ORGROOT', #{entCode})
ORDER BY A.SortKey ASC

SELECT COUNT(*) FROM ( SELECT BizDocProcessList.* FROM ( SELECT A.ProcessID ,A.InitiatorID ,A.InitiatorName ,A.InitiatorUnitID ,A.InitiatorUnitName , A.startDate AS startDate , A.endDate AS endDate , A.DeleteDate AS DeleteDate ,A.ProcessState ,A.InitiatorSIPAddress ,A.DocSubject ,B.Subject ,D.BizDocFormID ,E.BizDocID ,'T006' as SubKind ,C.* FROM JWF_PROCESS A INNER JOIN JWF_FORMINSTANCE B ON B.FormInstID = A.FormInstID INNER JOIN JWF_PROCESSDESCRIPTION C ON A.ProcessDescriptionID = C.ProcessDescriptionID INNER JOIN JWF_BIZDOCFORM D ON D.FormPrefix = C.FormPrefix INNER JOIN JWF_BIZDOC E ON D.BizDocID = E.BizDocID WHERE A.ProcessState = 288 AND E.BizDocCode = #{bizDocCode} AND A.DeleteDate IS NULL AND A.ParentProcessID = 0
AND B.EntCode = #{entCode}
AND B.EntCode IN ('ORGROOT', #{entCode})
) BizDocProcessList
startDate BETWEEN TO_DATE(#{startDate}, 'YYYY-MM-DD HH24:MI:SS') AND TO_DATE(#{endDate}, 'YYYY-MM-DD HH24:MI:SS') + 1
AND
FormSubject InitiatorUnitName InitiatorName FormName FormSubject
LIKE '%' || #{searchWord} || '%'
AND startDate BETWEEN TO_DATE(#{searchGroupWord}, 'YYYY-MM-DD HH24:MI:SS') AND TO_DATE(#{searchGroupWord}, 'YYYY-MM-DD HH24:MI:SS') + 1
AND
InitiatorID InitiatorUnitID FormPrefix
= #{searchGroupWord}
) A

SELECT COUNT(*) FROM ( SELECT ProcessID FROM ( SELECT A.ProcessID ,A.InitiatorID ,A.InitiatorName ,A.InitiatorUnitID ,A.InitiatorUnitName , A.startDate AS startDate , A.endDate AS endDate , A.DeleteDate AS DeleteDate ,A.ProcessState ,A.InitiatorSIPAddress ,A.DocSubject ,B.Subject ,'T006' as SubKind ,C.BizDocFormID ,D.BizDocID ,E.* FROM JWF_PROCESS A INNER JOIN JWF_FORMINSTANCE B ON B.FormInstID = A.FormInstID INNER JOIN JWF_BIZDOCFORM C ON C.FormPrefix = A.FormPrefix INNER JOIN JWF_BIZDOC D ON D.BizDocID = C.BizDocID INNER JOIN JWF_PROCESSDESCRIPTION E ON A.ProcessDescriptionID = E.ProcessDescriptionID WHERE A.DeleteDate IS NULL AND D.BizDocCode = #{bizDocCode} AND A.BusinessState like '02_01%' AND A.ParentProcessID = 0 AND A.ProcessState = 528
AND B.EntCode = #{entCode}
AND B.EntCode IN ('ORGROOT', #{entCode})
) BizDocCompleteList
endDate BETWEEN TO_DATE(#{startDate}, 'YYYY-MM-DD HH24:MI:SS') AND TO_DATE(#{endDate}, 'YYYY-MM-DD HH24:MI:SS') + 1
AND
FormSubject InitiatorUnitName InitiatorName FormName FormSubject
LIKE '%' || #{searchWord} || '%'
AND endDate BETWEEN TO_DATE(#{searchGroupWord}, 'YYYY-MM-DD HH24:MI:SS') AND TO_DATE(#{searchGroupWord}, 'YYYY-MM-DD HH24:MI:SS') + 1
AND
InitiatorID InitiatorUnitID FormPrefix
= #{searchGroupWord}
) A
